import { Injectable } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { BaseSiteService } from '@spartacus/core';
import { take, switchMap } from 'rxjs/operators';
import { UserAuthService } from './features/user/services/user-auth.service';
import { AppState } from './store/app-state';
import { UtilService } from './utilities/util.service';
import { WindowRef } from '@spartacus/core';

@Injectable({
  providedIn: 'root',
})
export class AppService {
  constructor(
    private _baseSiteService: BaseSiteService,
    private _appState: AppState,
    private utilService: UtilService,
    private userAuthService: UserAuthService,
    private route: ActivatedRoute,
    private _winRef: WindowRef
  ) {}

  public init() {
    this.load3dsScript();
    this.loadOneTimeToken();
    this._loadCurrentCountry();
    this.loadMpayScript();
  }

  public loadCMPScript() {
    this.appendScript2Head({
      src: this.utilService.getCMPScriptUrl(),
      type: 'module',
      async: true,
      crossorigin: 'anonymous',
      'data-stencil-namespace': 'cookie-banner',
    });
  }

  private loadOneTimeToken() {
    this.route.queryParams.subscribe((params) => {
      this.userAuthService.handleOneTimeToken(params);
    });
  }

  private _loadCurrentCountry(): void {
    this._baseSiteService
      .getActive()
      .pipe(
        take(1),
        switchMap((siteId: string) => {
          return this._baseSiteService.get(siteId);
        })
      )
      .subscribe((baseSiteData: any) => {
        this._appState.loadCurrentCountry(
          baseSiteData?.baseStore?.billingCountries[0]?.isocode
        );
      });
  }

  private loadMpayScript() {
    this.appendScript2Head({
      src: this.utilService.getMpayScriptUrl(),
      type: 'text/javascript',
      async: true,
    });
  }

  private load3dsScript() {
    this.appendScript2Head({
      src: this.utilService.get3dsScriptUrl(),
      type: 'text/javascript',
      async: true,
    });
  }

  private appendScript2Head(attributes?: any) {
    const script = this._winRef.document.createElement('script');
    if (attributes) {
      Object.keys(attributes).forEach((key) => {
        script.setAttribute(key, (attributes as any)[key]);
      });
      this._winRef.document.head.appendChild(script);
    }
  }
}

