import {Directive, ElementRef, Output, EventEmitter, HostListener} from '@angular/core';

@Directive({
  selector: '[atcppsClickedOutside]'
})
export class ClickedOutsideDirective {

  constructor(private el: ElementRef) { }

  @Output() public clickedOutside = new EventEmitter();

  @HostListener('document:click', ['$event.target'])
  public onClick(target: any) {
      const clickedInside = this.el.nativeElement.contains(target);
      if (!clickedInside) {
        this.clickedOutside.emit(target);
      }
  }

}

import { Directive, Input, TemplateRef, ViewContainerRef } from '@angular/core';
import { UtilService } from 'src/app/utilities/util.service';

@Directive({
  selector: '[atcppsShowIfCountry]',
})
export class MarketSpecificDirective {
  constructor(
    private _templateRef: TemplateRef<any>,
    private _viewContainerRef: ViewContainerRef,
    private _utilService: UtilService
  ) {}

  @Input() set atcppsShowIfCountry(countries: string[]) {
    const currentCountry = this._utilService.getActiveCountryIsoCode();
    if (countries.includes(currentCountry))
      this._viewContainerRef.createEmbeddedView(this._templateRef);
  }
}

