import * as dayjs from 'dayjs';
import { Observable, Subscription } from 'rxjs';
import { DsdAppointmentHelperService } from '../../services/dsd-appointment-helper.service';
import {
  AvailableDate,
  AvailableDates,
  DATE_FORMATS,
  DSDAppointment,
  DatePicker,
  Timeslot,
} from '../../models/appointment-booking.models';
import { OccCheckoutDeliveryModeService } from '../../../checkout/occ/occ-checkout-deliverymode.service';
import { map, shareReplay } from 'rxjs/operators';
import { OccDsdAppointmentAdapter } from '../../occ/occ-dsd-appointment-adapter';
import { ActiveCartService } from '@spartacus/cart/base/core';
import { Cart } from '@spartacus/cart/base/root';
import { UtilService } from 'src/app/utilities/util.service';
import { WindowRef } from '@spartacus/core';

@Component({
  selector: 'atcpps-dsd-appointment-booking',
  templateUrl: './timeslot-booking.component.html',
  styleUrls: ['./timeslot-booking.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TimeslotBookingComponent
  implements OnInit, AfterViewChecked, OnDestroy
{
  @Input() dealer: any;
  @Output() appointmentSaveCompleted = new EventEmitter();
  @Output() dsdSystemAvailable = new EventEmitter<boolean>();
  minDate = '';
  maxDate = '';
  selectedDate = '';
  selectedEndDate = '';
  timeslotDays$: Observable<AvailableDates>;
  dateFormat = 'YYYY-MM-DD';
  availableDates$: Observable<AvailableDates>;
  missingDates: string[];
  firstSlotOftheDay: any;
  selectedOption: string;
  isBlockoutDatesSet = false;
  onDatesLoadComplete = false;
  onDatesLoadError = false;
  currentlySelectedAppoint: DSDAppointment;
  savedAppointmentOnCart$: Observable<DSDAppointment | undefined>;
  subscription$ = new Subscription();
  locale = '';
  market = '';
  activeMonth: number;
  activeYear: number;
  activeTimeSlot: string;
  preSelectSlots?: Timeslot;

  constructor(
    private _dsdAppointmentHelper: DsdAppointmentHelperService,
    private occCheckoutDeliveryMode: OccCheckoutDeliveryModeService,
    private _occDsdAppointmentAdapter: OccDsdAppointmentAdapter,
    private cdRef: ChangeDetectorRef,
    private _activeCartService: ActiveCartService,
    private _utilService: UtilService,
    private _winRef: WindowRef
  ) {}
  ngOnDestroy(): void {
    this.subscription$.unsubscribe();
  }

  ngOnInit(): void {
    this.availableDates$ = this._occDsdAppointmentAdapter
      .getAvailableDates(this.dealer.id)
      .pipe(shareReplay(1));

    this.savedAppointmentOnCart$ = this._activeCartService.getActive().pipe(
      map((cart: Cart) => {
        return cart.appointment;
      })
    );
    this.market = this._utilService.getActiveCountryIsoCode();
    this.locale = this._utilService.getSiteContext('language')?.split('_')[0];
    if (this.locale) dayjs.locale(this.locale);
    this.fetchAvailableDates();
    this.cdRef.detectChanges();
  }

  ngAfterViewChecked(): void {
    if (this.missingDates?.length && !this.isBlockoutDatesSet) {
      this.setBlockedOutDates(this.missingDates);
    }
  }

  private fetchAvailableDates() {
    this.subscription$.add(
      this.availableDates$.subscribe({
        next: (value) => {
          this.dsdSystemAvailable.emit(true);
          this._processAvailableDates(value);
        },
        error: () => {
          this.onDatesLoadError = true;
          this.dsdSystemAvailable.emit(false);
          this.cdRef.detectChanges();
        },
        complete: () => {
          this.onDatesLoadComplete = true;
          this.cdRef.detectChanges();
        },
      })
    );
  }

  private _processAvailableDates(value: AvailableDates): void {
    const availableDates = value.availableDates.map(
      (item: AvailableDate) => item.id
    );
    [this.minDate] = availableDates;
    this.maxDate = availableDates[availableDates.length - 1];
    const allDates = this.generateDateRange(this.minDate, this.maxDate);
    this.missingDates = allDates.filter(
      (date) => !availableDates.includes(date)
    );
    this._setSavedSlotOnCalendar(this.minDate);
  }

  private _setSavedSlotOnCalendar(dateToBeSelected: string): void {
    this.subscription$.add(
      this.savedAppointmentOnCart$.subscribe((savedAppointment) => {
        const appointmentFromState =
          this._dsdAppointmentHelper.getSelectedAppointmentOnState();
        if (savedAppointment || appointmentFromState) {
          if (savedAppointment) {
            const bookedDate = savedAppointment.bookedDate;
            this.activeTimeSlot = bookedDate.split('T')[1];
          }

          const selectedAppoint = savedAppointment?.bookedDate
            ? savedAppointment
            : appointmentFromState;
          const selectedDate = selectedAppoint
            ? this._dsdAppointmentHelper.formatDate(
                selectedAppoint.bookedDate,
                DATE_FORMATS.DATE_FORMAT
              )
            : dateToBeSelected;

          if (selectedAppoint && dayjs(selectedDate).isBefore(this.minDate)) {
            this.selectedDate = dateToBeSelected;
            this.getAvailableSlots();
          } else {
            this.currentlySelectedAppoint = selectedAppoint;
            this.selectedDate = selectedDate;
            this.getAvailableSlots(selectedAppoint?.bookedDate);
          }
        } else {
          this.selectedDate = dateToBeSelected;
          this.getAvailableSlots();
        }
        this.cdRef.detectChanges();
      })
    );
  }

  private getAvailableSlots(lastSavedAppointId?: string) {
    this.timeslotDays$ = this.fetchTimeSlots(
      this.dealer.id,
      this.selectedDate,
      this.selectedDate
    );
    this.setTimeSlotOnCalendar(lastSavedAppointId);
  }

  private setBlockedOutDates(blockedOutDates: string[]) {
    const datepicker: DatePicker | null =
      this._winRef.document.querySelector('wb-datepicker');
    datepicker?.setBlockedDates(blockedOutDates);
    this.isBlockoutDatesSet = true;
  }

  private getFirstSlotFromAvailableDates(
    availableDates: AvailableDate[] | undefined
  ): Timeslot | undefined {
    const firstDaySlots = availableDates?.[0]?.timeslots;
    if (this.activeTimeSlot) {
      this.preSelectSlots = availableDates?.[0].timeslots?.find(
        (timeslot: Timeslot) => {
          return timeslot.id.includes(this.activeTimeSlot);
        }
      );
      return this.preSelectSlots ? this.preSelectSlots : firstDaySlots?.[0];
    } else {
      return firstDaySlots?.[0];
    }
  }

  private setTimeSlotOnCalendar(lastSavedAppointId?: string): void {
    this.subscription$.add(
      this.timeslotDays$.subscribe((data) => {
        this.firstSlotOftheDay = this.getFirstSlotFromAvailableDates(
          data.availableDates
        );
        this.selectedOption = lastSavedAppointId
          ? lastSavedAppointId
          : this.firstSlotOftheDay.id;
        this.currentlySelectedAppoint = {
          bookedDate: lastSavedAppointId
            ? lastSavedAppointId
            : this.firstSlotOftheDay.id,
          duration: this.firstSlotOftheDay.duration,
        };
      })
    );
  }

  compareDateTimeslot(timeSlotId: string): boolean {
    return (
      this._dsdAppointmentHelper.formatDate(
        timeSlotId,
        DATE_FORMATS.DATE_WITH_TIME_FORMAT
      ) ===
      this._dsdAppointmentHelper.formatDate(
        this.selectedOption,
        DATE_FORMATS.DATE_WITH_TIME_FORMAT
      )
    );
  }

  onOptionSelected(timeslotDetails: Timeslot) {
    this.selectedOption = timeslotDetails.id;
    this.activeTimeSlot = this.selectedOption.split('T')[1];
    this.currentlySelectedAppoint = {
      bookedDate: timeslotDetails.id,
      duration: String(timeslotDetails.duration),
    };
    this._dsdAppointmentHelper.storeSelectedAppointmentOnState(
      this.currentlySelectedAppoint
    );
  }

  private generateDateRange(startDate: string, endDate: string): string[] {
    const dateRange = [];
    let currentDate = dayjs(startDate);
    const endDay = dayjs(endDate);

    while (currentDate <= endDay) {
      dateRange.push(currentDate.format(this.dateFormat));
      currentDate = currentDate.add(1, 'day');
    }
    return dateRange;
  }

  selectionChanged(event: any) {
    if (event?.detail?.selectedDate) {
      this.selectedDate = event.detail.selectedDate;
    }

    if (this.selectedDate) {
      this.timeslotDays$ = this.fetchTimeSlots(
        this.dealer.id,
        this.selectedDate,
        this.selectedDate
      );
      this.setTimeSlotOnCalendar();
    }
  }

  private fetchTimeSlots(
    dealerId: string,
    startDate: string,
    endDate: string
  ): Observable<AvailableDates> {
    return this._occDsdAppointmentAdapter
      .getAvailableSlotsPerDay(dealerId, startDate, endDate)
      .pipe(shareReplay(1));
  }

  saveAppointmentOnCart() {
    this.subscription$.add(
      this.occCheckoutDeliveryMode
        .saveAppointmentToCart(this.currentlySelectedAppoint)
        .subscribe({
          complete: () => {
            this._onSuccess();
          },
        })
    );
  }

  getSelectedMonth(): number {
    return dayjs(this.selectedDate).month();
  }

  getSelectedYear(): number {
    return dayjs(this.selectedDate).year();
  }

  private _onSuccess() {
    this.appointmentSaveCompleted.emit();
  }
}

