import { AddToCartComponent } from '@spartacus/cart/base/components/add-to-cart';
import {
  ChangeDetectorRef,
  Component,
  ComponentRef,
  OnDestroy,
  OnInit,
  Optional,
} from '@angular/core';
import { CustomProduct } from '../../../models/mb-models';
import { filter, map, take } from 'rxjs/operators';
import { ActiveCartFacade } from '@spartacus/cart/base/root';
import { CurrentProductService, CmsComponentData } from '@spartacus/storefront';
import { EventService, CmsAddToCartComponent } from '@spartacus/core';
import { BrowserStorageService } from 'src/app/store/browser-storage.service';
import { DealerLocatorService } from '../../dealer-locator/services/dealer-locator.service';
import { Observable, Subscription, combineLatest, of } from 'rxjs';
import { ProductListItemContext } from '@spartacus/storefront';
import { VehicleStateService } from '../../vehicle-configurator/state/vehicle-state.service';
import {
  COMPATIBILITY_MATRIX,
  COMPATIBILITY_OUTPUTS,
} from '../../vehicle-configurator/core/models/vehicle.models';
import { ProductCompatibilityService } from '../../vehicle-configurator/core/services/product-compatibility.service';
import { isNotNullable } from '@spartacus/core';

@Component({
  selector: 'cx-add-to-cart',
  templateUrl: './custom-add-to-cart.component.html',
  styleUrls: ['./custom-add-to-cart.component.scss'],
})
export class CustomAddToCartComponent
  extends AddToCartComponent
  implements OnInit, OnDestroy
{
  isDealer: boolean;
  dealerId: string;
  vinNumberSubscription: Subscription;
  compatibiltyCheckTypes: typeof COMPATIBILITY_MATRIX = COMPATIBILITY_MATRIX;
  compatibiltyCheckOutputs: typeof COMPATIBILITY_OUTPUTS =
    COMPATIBILITY_OUTPUTS;

  disableAddToCartCTA$: Observable<boolean> = combineLatest([
    this.currentProductService.getProduct(),
    this._vehicleStateService.compatibilityCheckResult$,
  ]).pipe(
    map(
      ([product, compatibilityCheckResult]) =>{
        return (product?.compatibilityCheck === COMPATIBILITY_MATRIX.MANDATORY &&
        compatibilityCheckResult?.status !== COMPATIBILITY_OUTPUTS.COMPATIBLE)
      }
    )
  );
  constructor(
    private _browserStorageService: BrowserStorageService,
    protected override currentProductService: CurrentProductService,
    protected override cd: ChangeDetectorRef,
    protected override activeCartService: ActiveCartFacade,
    protected override component: CmsComponentData<CmsAddToCartComponent>,
    protected override eventService: EventService,
    private dealerLocatorService: DealerLocatorService,
    private _vehicleStateService: VehicleStateService,
    private _productCompatibilityService: ProductCompatibilityService,
    @Optional() override productListItemContext?: ProductListItemContext
  ) {
    super(
      currentProductService,
      cd,
      activeCartService,
      component,
      eventService,
      productListItemContext
    );
  }

  override ngOnInit() {
    this.currentProductService.getProduct().subscribe((item: any) => {
      this.dealerLocatorService.storeSelectedDealer(item?.isDealerAvailable);
      this.isDealer = item?.isDealerAvailable;
    });

    super.ngOnInit();
    this._checkCCStatusForProduct();
  }

  private _checkCCStatusForProduct() {
    this.vinNumberSubscription = combineLatest([
      this._productCompatibilityService.cartEntryNeedsVin(),
      this._vehicleStateService.vinVehicleData$,
      this.currentProductService.getProduct(),
    ])
      .pipe(
        filter(([, , product]) => isNotNullable(product?.compatibilityCheck))
      )
      .subscribe(([flag, vehicleData, product]) => {
        if (flag && product?.compatibilityCheck)
          this._vehicleStateService.setProductVinNumber(vehicleData?.vin);
        else this._vehicleStateService.setProductVinNumber(undefined);
      });
  }

  protected override setStockInfo(product: CustomProduct): void {
    this.quantity = 1;
    this.hasStock = Boolean(product.stock?.stockLevelStatus !== 'outOfStock');

    this.inventoryThreshold = product.stock?.isValueRounded ?? false;

    if (this.hasStock && product.stock?.stockLevel) {
      this.maxQuantity =
        product.stock.stockLevel >= product.maxOrderQuantity
          ? product.maxOrderQuantity
          : product.stock.stockLevel;
    } else if (this.hasStock) {
      this.maxQuantity = product.maxOrderQuantity;
    }

    if (this.productListItemContext) {
      this.showQuantity = false;
    }
  }

  override addToCart() {
    if (this.isDealer) {
      const dealerIdFromLocal = of(
        JSON.parse(this._browserStorageService.readItem('dealer_details') ?? '')
      );
      dealerIdFromLocal.subscribe((item: any) => {
        this.dealerId = item.name;
      });
    }

    const quantity = this.addToCartForm.get('quantity')?.value;
    if (!this.productCode || quantity <= 0) {
      return;
    }

    if (this.pickupOptionCompRef instanceof ComponentRef) {
      this.pickupOptionCompRef.instance.intendedPickupLocation$
        .pipe(take(1))
        .subscribe((intendedPickupLocation: any) => {
          this.pickupStore =
            intendedPickupLocation?.pickupOption === 'pickup'
              ? intendedPickupLocation.name
              : undefined;
        });
    }

    this.activeCartService
      .getEntries()
      .pipe(take(1))
      .subscribe((cartEntries) => {
        this.activeCartService.addEntry(
          this.productCode,
          quantity,
          this.isDealer ? this.dealerId : ''
        );


        // A CartUiEventAddToCart is dispatched.  This event is intended for the UI
        // responsible to provide feedback about what was added to the cart, like
        // the added to cart dialog.
        //
        // Because we call activeCartService.getEntries() before, we can be sure the
        // cart library is loaded already and that the event listener exists.
        this.eventService.dispatch(
          this.createCartUiEventAddToCart(
            this.productCode,
            quantity,
            cartEntries.length,
            this.isDealer ? this.dealerId : ''
          )
        );
      });
  }

  override ngOnDestroy() {
    super.ngOnDestroy();
    this.vinNumberSubscription?.unsubscribe();
  }
}

