import {
  Component,
  ComponentRef,
  EventEmitter,
  Input,
  OnDestroy,
  Output,
  ViewContainerRef,
} from '@angular/core';
import { Observable, Subject, Subscription, timer } from 'rxjs';
import { OccCheckoutPlaceOrderService } from '../../../occ/occ-checkout-place-order.service';
import { MultiCartFacade } from '@spartacus/cart/base/root';
import { GlobalMessageService } from '@spartacus/core';
import { GlobalMessageType } from '@spartacus/core';
import { TranslationService } from '@spartacus/core';
import { LaunchDialogService } from '@spartacus/storefront';
import { LAUNCH_CALLER } from '@spartacus/storefront';
import { AppState } from 'src/app/store/app-state';
import { RoutingService } from '@spartacus/core';
import { map, switchMap, takeUntil } from 'rxjs/operators';
import { CheckoutPaymentService } from '@spartacus/checkout/base/core';
import { Dialog3DSLoadingService } from '../custom-checkout-3ds-dialog/custom-checkout-3ds-dialog.service';
import { PaymentAuthStatus } from '../../../models/checkout.models';
import { UtilService } from 'src/app/utilities/util.service';
import { WindowRef } from '@spartacus/core';
declare const window: any;

@Component({
  selector: 'atcpps-checkout-3ds-payments',
  templateUrl: './custom-checkout-3ds-payments.component.html',
  styleUrls: ['./custom-checkout-3ds-payments.component.scss'],
})
export class CustomCheckout3DSPaymentsComponent implements OnDestroy {
  preAuthSub: Subscription | undefined;
  threeDSDialogComponent: void | Observable<ComponentRef<any> | undefined>;
  paymentData: any;
  currentCartId = '';
  userGuid: string;
  stopTimer: Subject<void> = new Subject();

  @Input() placedOrder: any;
  @Input() userId: any;
  @Input() onSubmitSubscription: Subscription | undefined;
  @Output() submitOrder = new EventEmitter();
  @Output() setPaymentData = new EventEmitter();

  constructor(
    private vcr: ViewContainerRef,
    private _occCheckoutPlaceOrderService: OccCheckoutPlaceOrderService,
    private multiCartFacade: MultiCartFacade,
    private globalMessage: GlobalMessageService,
    private translationService: TranslationService,
    private launchDialogService: LaunchDialogService,
    private appState: AppState,
    private utilService: UtilService,
    private routingService: RoutingService,
    private checkoutPaymentService: CheckoutPaymentService,
    private dialog3DSLoadingService: Dialog3DSLoadingService,
    private _winRef: WindowRef
  ) {}

  public verify3DSPayments(userId: string, cartId: string) {
    if (userId && cartId) {
      this.currentCartId = cartId;
      window.UPI3DS2.getDeviceInformation({
        callback: (err: object, deviceInformation: any) => {
          if (err) {
            this.handle3DSError(err, 'checkoutReview.auth.failed');
          } else {
            this.preAuthSub = this._occCheckoutPlaceOrderService
              .postDeviceInformation(userId, cartId, deviceInformation)
              .subscribe(
                (res: any) => {
                  this.paymentData = res;
                  this.setPaymentData.emit(this.paymentData);
                  this.initiate3DSFlow(res, false);
                },
                (error: object) => {
                  this.handle3DSError(error, 'checkoutReview.auth.failed');
                }
              );
          }
        },
      });
    } else {
      this.handle3DSError({}, 'checkoutReview.auth.failed');
    }
  }

  public initiate3DSFlow(data: any, retry: boolean) {
    this.enablePlaceOrderButton(false);
    if (data?.threeDSecure2Challenge) {
      retry
        ? this.verify3DSPayments(this.userId, this.currentCartId)
        : this.render3DSChallenge(data.threeDSecure2Challenge);
    } else if (data?.threeDSecure2Fingerprint) {
      retry
        ? this.verify3DSPayments(this.userId, this.currentCartId)
        : this.generateDeviceFingerprint(data?.threeDSecure2Fingerprint);
    } else if (data?.redirectUrl) {
      this.handle3DS1Authentication(this.userId, data);
    } else {
      this.submitOrder.emit();
    }
  }

  private generateDeviceFingerprint(threeDSecure2Fingerprint: string) {
    if (threeDSecure2Fingerprint) {
      window.UPI3DS2.generateDeviceFingerprint(threeDSecure2Fingerprint, {
        elem: this._winRef.document.getElementById('deviceFingerpintWrapper'),
        callback: (err: any, transaction_status: any) => {
          if (err) {
            this.handle3DSError(
              this.generate3DSRetryError(),
              'checkoutReview.auth.failed'
            );
          } else {
            const threeDSecure2Challenge =
              transaction_status?.asynchronous?.threeDSecure2Challenge;
            if (transaction_status?.status === PaymentAuthStatus.AUTHORIZED) {
              this.submitOrder.emit();
            } else if (threeDSecure2Challenge) {
              this.render3DSChallenge(threeDSecure2Challenge);
            } else {
              this.handle3DSError({}, 'checkoutReview.auth.failed');
            }
          }
        },
      });
    } else {
      this.handle3DSError({}, 'checkoutReview.auth.failed');
    }
  }

  private render3DSChallenge(threeDSecure2Challenge: any) {
    this.dialog3DSLoadingService.setLoading(false);
    this.threeDSDialogComponent = this.launchDialogService.launch(
      'THREE_DS_DIALOG',
      this.vcr
    );

    if (threeDSecure2Challenge) {
      window.UPI3DS2.showChallenge(threeDSecure2Challenge, {
        elem: this._winRef.document.getElementById('threeDSChallengeWrapper'),
        callback: (err: any, transaction_status: any) => {
          if (!transaction_status || err) {
            this.handle3DSError(
              this.generate3DSRetryError(),
              'checkoutReview.auth.failed'
            );
          } else if (transaction_status) {
            this.submitOrder.emit();
          }
        },
        size: '02',
      });
    }
  }

  private handle3DS1Authentication(userId: string, res: any) {
    const transactionId: string = res?.transactionId ?? '';
    const redirectUrl =
      this.utilService.getMpayBaseUrl() + (res?.redirectUrl ?? '');
    const authTabInstance = window.open(redirectUrl, '_blank');
    if (!authTabInstance || authTabInstance?.closed) {
      this.handle3DSError({}, 'checkoutReview.auth.popup');
    } else {
      this.getTransactionStatus(userId, transactionId, authTabInstance);
    }
  }

  private getTransactionStatus(
    userId: string,
    transactionId: string,
    authTabInstance: any
  ) {
    timer(0, 3000)
      .pipe(
        switchMap(() =>
          this._occCheckoutPlaceOrderService.getTransactionStatus(
            userId,
            transactionId
          )
        ),
        takeUntil(this.stopTimer)
      )
      .subscribe({
        next: (res: any) => {
          const transactionStatusReview =
            res?.transactionStatus === PaymentAuthStatus.REVIEW;
          if (!transactionStatusReview) {
            this.stopTimer.next();
            this.closeAuthTab(authTabInstance);
            this.onPaymentAuthStatusChange(res?.transactionStatus);
          }
          if (authTabInstance.closed) {
            this.stopTimer.next();
            const transactionStatusRejected =
              res?.transactionStatus === PaymentAuthStatus.REJECTED;
            this.resetOrderSubmission(
              transactionStatusReview || transactionStatusRejected
                ? true
                : false
            );
          }
        },
        error: (error: object) => {
          this.handle3DSError(error, 'checkoutReview.auth.failed');
        },
      });
  }

  private onPaymentAuthStatusChange(transactionStatus: string) {
    if (transactionStatus === PaymentAuthStatus.ACCEPTED) {
      this.submitOrder.emit();
    } else if (transactionStatus === PaymentAuthStatus.ERROR) {
      this.handle3DSError({}, 'checkoutReview.auth.failed');
    } else if (transactionStatus === PaymentAuthStatus.REJECTED) {
      this.paymentData = undefined;
      this.setPaymentData.emit(this.paymentData);
      this.paymentDetailsAllowMultipleTransactions()
        .subscribe((savedPaymentDetails) => {
          !savedPaymentDetails
            ? this.resetCheckoutPaymentOptions()
            : this.handle3DSError({}, 'checkoutReview.auth.failed');
        })
        .unsubscribe();
    }
  }

  private handle3DSError(err: any | undefined, key: string, message?: string) {
    if (
      err?.error?.errors[0]?.type === 'CartError' &&
      err?.error?.errors[0]?.reason === 'expired' &&
      err?.error?.errors[0]?.subject
    ) {
      this.userGuid = err?.error?.errors[0]?.subject;
      this.currentCartId = this.userGuid ? this.userGuid : '';
      //Update cart on transaction attempt if anonymous user guid has been updated
      this.reloadActiveCart(this.userId, this.userGuid);
      this.verify3DSPayments(this.userId, this.userGuid);
    } else if (err?.error?.errors[0]?.type === 'PaymentAuthorizationError') {
      this.paymentDetailsAllowMultipleTransactions()
        .subscribe((savedPaymentDetails) => {
          !savedPaymentDetails
            ? this.resetCheckoutPaymentOptions()
            : this.showUserErrorMsg(
                err?.error?.errors[0]?.message ?? message ?? '',
                key
              );
          this.resetOrderSubmission(true);
        })
        .unsubscribe();
    } else {
      this.showUserErrorMsg(
        err?.error?.errors[0]?.message ?? message ?? '',
        key
      );
      this.resetOrderSubmission(false);
    }
  }

  public resetOrderSubmission(enablePlaceOrder: boolean) {
    this.clearThreeDSDialog();
    this.clearPlaceOrderSpinner();
    this.onSubmitSubscription?.unsubscribe();
    this.preAuthSub?.unsubscribe();
    this.enablePlaceOrderButton(enablePlaceOrder);
  }

  private clearThreeDSDialog() {
    this.threeDSDialogComponent
      ?.subscribe((comp: any) => {
        this.launchDialogService.clear('THREE_DS_DIALOG');
        comp?.destroy();
      })
      .unsubscribe();
  }

  private clearPlaceOrderSpinner() {
    this.launchDialogService.clear(LAUNCH_CALLER.PLACE_ORDER_SPINNER);
    this.placedOrder
      ?.subscribe((component: any) => {
        this.launchDialogService.clear(LAUNCH_CALLER.PLACE_ORDER_SPINNER);
        if (component) component.destroy();
      })
      .unsubscribe();
  }

  private reloadActiveCart(userId: string, cartId: string) {
    this.onSubmitSubscription?.unsubscribe();
    this.multiCartFacade.loadCart({
      cartId: cartId,
      userId: userId,
      extraData: { active: true },
    });
  }

  private showUserErrorMsg(errorMsg: string, key: string) {
    this.translationService.translate(key).subscribe((text: string) => {
      if (!errorMsg) errorMsg = text;
    });
    this.showErrorMessage(errorMsg);
  }

  public showErrorMessage(errorMessage: string) {
    this.globalMessage.add(
      errorMessage,
      GlobalMessageType.MSG_TYPE_ERROR,
      6000
    );
  }

  public enablePlaceOrderButton(enabled: boolean) {
    const placeOrderButton = this._winRef.document.getElementById(
      'placeOrder'
    ) as HTMLButtonElement;
    if (placeOrderButton) placeOrderButton.disabled = !enabled;
  }

  private closeAuthTab(instance: any) {
    if (instance) instance.close();
  }

  private generate3DSRetryError() {
    return { error: { errors: [{ type: 'PaymentAuthorizationError' }] } };
  }

  private paymentDetailsAllowMultipleTransactions(): Observable<boolean> {
    return this.checkoutPaymentService
      .getPaymentDetailsState()
      .pipe(map((payload: any) => payload?.data?.saved));
  }

  private resetCheckoutPaymentOptions() {
    this.appState.setTransactionRetry(true);
    this.routingService.go({ cxRoute: 'checkoutPaymentDetails' });
    this.showUserErrorMsg('', 'checkoutReview.auth.rejected');
  }

  ngOnDestroy() {
    if (this.onSubmitSubscription) this.onSubmitSubscription.unsubscribe();
    if (this.preAuthSub) this.preAuthSub.unsubscribe();
    this.stopTimer.next();
  }
}

import { Component, OnDestroy, OnInit } from '@angular/core';
import { Observable, BehaviorSubject, combineLatest, Subscription } from 'rxjs';
import {
  Country,
  Region,
  Title,
  UserAddressService,
  Address,
  UserPaymentService,
  getLastValueSync,
  TranslationService,
  UserService,
} from '@spartacus/core';
import { filter, map, tap, switchMap } from 'rxjs/operators';
import {
  UntypedFormGroup,
  UntypedFormBuilder,
  Validators,
} from '@angular/forms';
import { Card, sortTitles } from '@spartacus/storefront';
import {
  CheckoutDeliveryAddressService,
  CheckoutPaymentService,
} from '@spartacus/checkout/base/core';
import { countryConfig } from 'src/app/countries/country-config';
import { UtilService } from 'src/app/utilities/util.service';
import { ActiveCartService } from '@spartacus/cart/base/core';
import { AddressType } from 'src/app/features/product/models/mb.product.model';
import { StoreKeys } from 'src/app/store/store-keys';
import { FormValidator } from 'src/app/shared/formValidator';
import { BrowserStorageService } from 'src/app/store/browser-storage.service';

@Component({
  selector: 'atcpps-billing-address-form',
  templateUrl: './custom-billing-address-form.component.html',
  styleUrls: ['./custom-billing-address-form.component.scss'],
})
export class CustomBillingAddressFormComponent implements OnInit, OnDestroy {
  deliveryAddress$: Observable<Address | undefined>;
  countries$: Observable<Country[]>;
  titles$: Observable<Title[]>;
  sameAsDeliveryAddress = true;
  regions$: Observable<Region[]>;
  selectedCountry$: BehaviorSubject<string> = new BehaviorSubject<string>('');
  showSameAsDeliveryAddressCheckbox$: Observable<boolean>;
  currentAddress: Address | undefined;

  billingAddressForm: UntypedFormGroup = this.fb.group({
    titleCode: ['', Validators.required],
    firstName: ['', Validators.required],
    lastName: ['', Validators.required],
    line1: ['', Validators.required],
    line2: [''],
    town: ['', Validators.required],
    region: this.fb.group({
      isocodeShort: [null, Validators.required],
    }),
    country: this.fb.group({
      isocode: [null, Validators.required],
    }),
    postalCode: ['', Validators.required],
  });

  subscription: Subscription = new Subscription();

  constructor(
    protected fb: UntypedFormBuilder,
    protected checkoutDeliveryAddressService: CheckoutDeliveryAddressService,
    protected userAddressService: UserAddressService,
    protected userPaymentService: UserPaymentService,
    private _utilService: UtilService,
    private activeCartService: ActiveCartService,
    private _checkoutPaymentService: CheckoutPaymentService,
    private browserStorageService: BrowserStorageService,
    private translation: TranslationService,
    private userService: UserService,
    private _formValidator: FormValidator
  ) {}

  ngOnInit(): void {
    // Fetching titles
    this.titles$ = this.getTitles();
    this.loadAddresses();
    this.countries$ = this.userPaymentService.getAllBillingCountries().pipe(
      tap((countries) => {
        // If the store is empty fetch countries. This is also used when changing language.
        if (Object.keys(countries).length === 0) {
          this.userPaymentService.loadBillingCountries();
        }
      })
    );
    this.deliveryAddress$ = this.checkoutDeliveryAddressService
      .getDeliveryAddressState()
      .pipe(
        filter((state) => !state.loading),
        map((state) => state.data)
      );

    this.showSameAsDeliveryAddressCheckbox$ = combineLatest([
      this.countries$,
      this.deliveryAddress$,
      this.userAddressService.getAddresses(),
      this._checkoutPaymentService.getPaymentDetailsState().pipe(
        filter((state) => !state.loading),
        map((state) => state.data)
      ),
    ]).pipe(
      map(([countries, address, currentAddress, updatedBillingAddress]) => {
        const countryCheck =
          (address?.country &&
            !!countries.filter(
              (country: Country): boolean =>
                country.isocode === address.country?.isocode
            ).length) ??
          false;
        const addressToBeUpdate =
          updatedBillingAddress?.billingAddress || currentAddress[0];
        return {
          shouldShowCheckbox: countryCheck,
          addressToBeUpdate: addressToBeUpdate,
        };
      }),
      tap((obg: any) => {
        this.sameAsDeliveryAddress = this.checkBoxState(obg.addressToBeUpdate);
        this.billingAddressForm.patchValue(obg.addressToBeUpdate);
      })
    );

    this.regions$ = this.selectedCountry$.pipe(
      switchMap((country) => this.userAddressService.getRegions(country)),
      tap((regions) => {
        const regionControl = this.billingAddressForm.get(
          'region.isocodeShort'
        );
        if (regions.length > 0) {
          regionControl?.enable();
        } else {
          regionControl?.disable();
        }
      })
    );
    this._customFormValidatorIsRequired();
    this.subscription = this.countries$.subscribe((countries) => {
      if (countries.length === 1) {
        this.billingAddressForm
          .get(['country', 'isocode'])
          ?.setValue(countries[0].isocode);
      }
    });
  }

  getTitles(): Observable<Title[]> {
    return combineLatest([
      this.translation.translate('addressForm.defaultTitle'),
      this.userService.getTitles(),
    ]).pipe(
      map(([noneTitleText, titles]) => {
        const noneTitle = { code: '', name: noneTitleText };
        titles.sort(sortTitles);
        return [noneTitle, ...titles];
      })
    );
  }

  toggleSameAsDeliveryAddress(): void {
    this.sameAsDeliveryAddress = !this.sameAsDeliveryAddress;
    this.browserStorageService.storeItem(
      StoreKeys.SameAsDeliveryAddress,
      JSON.stringify(this.sameAsDeliveryAddress)
    );
  }

  checkBoxState(address: Address | undefined): boolean {
    const checkboxState = JSON.parse(
      this.browserStorageService.readItem(StoreKeys.SameAsDeliveryAddress)!
    );
    if (address === undefined) {
      this.browserStorageService.storeItem(
        StoreKeys.SameAsDeliveryAddress,
        JSON.stringify(this.sameAsDeliveryAddress)
      );
      return checkboxState === null
        ? this.sameAsDeliveryAddress
        : checkboxState;
    } else {
      checkboxState === null &&
        this.browserStorageService.storeItem(
          StoreKeys.SameAsDeliveryAddress,
          JSON.stringify(!this.sameAsDeliveryAddress)
        );
      return checkboxState === null
        ? !this.sameAsDeliveryAddress
        : checkboxState;
    }
  }

  getAddressCardContent(address: Address): Card {
    let region = '';
    if (address.region && address.region.isocode) {
      region = address.region.isocode + ', ';
    }

    const currentConfig = countryConfig.find(
      (obj) => obj.countryCode === this._utilService.getActiveCountryIsoCode()
    );
    if (
      currentConfig &&
      currentConfig.formatAddress &&
      currentConfig.addressFormat
    ) {
      return currentConfig.addressFormat(null, address, AddressType.CHECKOUT);
    } else {
      return {
        textBold: address.firstName + ' ' + address.lastName,
        text: [
          address.line1,
          address.line2,
          address.town + ', ' + region + address.country?.isocode,
          address.postalCode,
          address.phone,
        ],
      } as Card;
    }
  }

  get isGuestCheckout(): boolean {
    return !!getLastValueSync(this.activeCartService.isGuestCart());
  }

  protected loadAddresses(): void {
    if (!this.isGuestCheckout) {
      this.userAddressService.loadAddresses();
    }
  }

  countrySelected(country: Country): void {
    this.billingAddressForm.get('country.isocode')?.setValue(country.isocode);
    this.selectedCountry$.next(country.isocode as string);
  }

  private _customFormValidatorIsRequired(): void {
    const currentConfig = countryConfig.find(
      (obj) => obj.countryCode === this._utilService.getActiveCountryIsoCode()
    );

    //TODO THis country flag should come from store
    if (currentConfig && currentConfig.customAddressFormValidatorRequired) {
      this.billingAddressForm = this._formValidator.formValidation;
    }
  }
  ngOnDestroy() {
    this.subscription.unsubscribe();
  }
}

import {
  Component,
  OnInit,
  OnDestroy,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  ViewChild,
  EventEmitter,
  Output,
} from '@angular/core';
import { UntypedFormBuilder } from '@angular/forms';
import { ActiveCartFacade } from '@spartacus/cart/base/root';
import { CheckoutPaymentFormComponent } from '@spartacus/checkout/base/components';
import {
  CheckoutPaymentFacade,
  CheckoutDeliveryAddressFacade,
} from '@spartacus/checkout/base/root';
import {
  UserPaymentService,
  GlobalMessageService,
  UserAddressService,
  AuthService,
} from '@spartacus/core';
import { LaunchDialogService } from '@spartacus/storefront';
import { combineLatest, Subscription } from 'rxjs';
import { debounceTime, map, switchMap, tap } from 'rxjs/operators';
import { OccCheckoutPaymentService } from '../../../occ/occ-checkout-payment.service';
import { UtilService } from 'src/app/utilities/util.service';
import { Country, TranslationService, getLastValueSync } from '@spartacus/core';
import { CustomBillingAddressFormComponent } from '../custom-billing-address-form/custom-billing-address-form.component';
import { formCustomisation } from '../../../models/checkout-iframe.styles';
import { ExtendedPaymentDetails } from '../../../models/checkout.models';
import { GlobalMessageType } from '@spartacus/core';
import { CustomCheckoutService } from '../../../services/custom-checkout.service';
import { WindowRef } from '@spartacus/core';

declare const window: any;
@Component({
  selector: 'atcpps-custom-checkout-payment-form',
  templateUrl: './custom-checkout-payment-form.component.html',
  styleUrls: ['./custom-checkout-payment-form.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomCheckoutPaymentFormComponent
  extends CheckoutPaymentFormComponent
  implements OnInit, OnDestroy
{
  ngOnDestroy(): void {
    if (this.tokenSub) {
      this.tokenSub.unsubscribe();
    }
    if (this.updatePaymentSub) {
      this.updatePaymentSub.unsubscribe();
    }
    if (this.mpayInit && !this.mpayInit?.success) {
      this.mpayInit.close();
    }
  }

  public cardDetails: ExtendedPaymentDetails;
  private isLoggedIn: boolean | undefined;
  private cartId: string | undefined;
  private tokenSub: Subscription | undefined;
  private updatePaymentSub: Subscription | undefined;
  private mpayToken = '';
  private mpayInit: any;
  toggleLoader = true;

  @Output()
  addedNewCard = new EventEmitter<any>();

  get isGuestCheckout(): boolean {
    return !!getLastValueSync(this.activeCartService.isGuestCart());
  }

  @ViewChild('customBillingAddressForm')
  billingAddressFormComponent: CustomBillingAddressFormComponent;
  constructor(
    checkoutPaymentFacade: CheckoutPaymentFacade,
    checkoutDeliveryAddressFacade: CheckoutDeliveryAddressFacade,
    userPaymentService: UserPaymentService,
    globalMessageService: GlobalMessageService,
    fb: UntypedFormBuilder,
    userAddressService: UserAddressService,
    launchDialogService: LaunchDialogService,
    private occCheckoutPaymentService: OccCheckoutPaymentService,
    private authService: AuthService,
    private activeCartService: ActiveCartFacade,
    protected cd: ChangeDetectorRef,
    private _utilService: UtilService,
    private customCheckoutService: CustomCheckoutService,
    private _winRef: WindowRef,
    override translationService: TranslationService
  ) {
    super(
      checkoutPaymentFacade,
      checkoutDeliveryAddressFacade,
      userPaymentService,
      globalMessageService,
      fb,
      userAddressService,
      launchDialogService,
      translationService
    );
  }

  override ngOnInit() {
    super.ngOnInit();
    this.getMpayToken();
    this.loadAddresses();

    //FIXME This should be removed later
    this.showSameAsDeliveryAddressCheckbox$ = combineLatest([
      this.countries$,
      this.deliveryAddress$,
      this.userAddressService.getAddresses(),
    ]).pipe(
      map(([countries, address, currentAddress]) => {
        const countryCheck =
          (address?.country &&
            !!countries.filter(
              (country: Country): boolean =>
                country.isocode === address.country?.isocode
            ).length) ??
          false;
        return {
          shouldShowCheckbox: countryCheck,
          currentAddress: currentAddress,
        };
      }),
      tap((obg: any) => {
        if (obg.currentAddress?.length !== 0) {
          obg.currentAddress.filter((address: { [key: string]: any }) => {
            this.billingAddressFormComponent.billingAddressForm.patchValue(
              address
            );
          });
          this.sameAsDeliveryAddress = !obg.shouldShowCheckbox;
        } else {
          this.sameAsDeliveryAddress = obg.shouldShowCheckbox;
        }
      })
    );
  }
  protected loadAddresses(): void {
    if (!this.isGuestCheckout) {
      this.userAddressService.loadAddresses();
    }
  }

  backToPrev(event: Event) {
    event.preventDefault();
    event.stopImmediatePropagation();
    this.goBack.emit();
  }

  nextStep(event: Event): void {
    event.preventDefault();
    this.billingAddressFormComponent.billingAddressForm.markAllAsTouched();
    if (!this.cardDetails) {
      this.showMpayErrorMessage('checkoutReview.cardFailiure');
      return;
    }
    if (this.billingAddressFormComponent.sameAsDeliveryAddress) {
      this.setPaymentDetails.emit({
        paymentDetails: this.paymentForm.value,
        billingAddress: null,
        userLoggedIn: this.isLoggedIn,
        cartId: this.cartId,
      });
    } else {
      if (this.billingAddressFormComponent.billingAddressForm.valid) {
        this.setPaymentDetails.emit({
          paymentDetails: this.paymentForm.value,
          billingAddress:
            this.billingAddressFormComponent.billingAddressForm.value,
          userLoggedIn: this.isLoggedIn,
          cartId: this.cartId,
        });
      } else {
        this.billingAddressFormComponent.billingAddressForm.markAllAsTouched();
      }
    }
  }

  private getMpayToken(): void {
    this.toggleLoader = true;
    this.cd.detectChanges();
    this.tokenSub = combineLatest([
      this.authService.isUserLoggedIn(),
      this.activeCartService.getActiveCartId(),
    ])
      .pipe(
        debounceTime(0),
        switchMap(([loginStatus, cartId]) => {
          this.isLoggedIn = loginStatus;
          this.cartId = cartId;
          return this.occCheckoutPaymentService.getMpayToken(
            loginStatus,
            cartId
          );
        })
      )
      .subscribe((data) => {
        this.mpayToken = data.parameters.entry[0].value;
        this.toggleLoader = false;
        this.cd.detectChanges();
        this.loadMpayForm();
      });
  }

  private loadMpayForm(): void {
    if (this.mpayToken) {
      this.mpayInit = window.UPI.init(this.mpayToken, {
        elem: this._winRef.document.getElementById('mPayPaymentForm'),
        lang: this.customCheckoutService.getMpayFormLanguage(),
        formCustomisation: formCustomisation,
        callback: (err: object, asset: any) => {
          if (err) {
            this.onError();
          } else {
            this.onSuccess(asset);
            this.mpayInit.success = true;
          }
        },
      });
    }
  }

  private onSuccess(asset: object): void {
    this.updatePaymentSub = this.occCheckoutPaymentService
      .updatePaymentDetails(this.isLoggedIn, this.cartId, asset)
      .subscribe((paymentDetails: ExtendedPaymentDetails) => {
        this.cardDetails = paymentDetails;
        this.addedNewCard.emit({
          paymentDetails: paymentDetails,
          userLoggedIn: this.isLoggedIn,
          cartId: this.cartId,
        });
        this.cd.detectChanges();
      });
  }

  private onError(): void {
    setTimeout(() => {
      this.loadMpayForm();
      this.showMpayErrorMessage('checkoutReview.cardFailiure');
      this.cd.detectChanges();
    }, 500);
  }

  private showMpayErrorMessage(key: string): void {
    this.translationService
      .translate(key)
      .subscribe((text: string) => {
        this.globalMessageService.add(
          text,
          GlobalMessageType.MSG_TYPE_ERROR,
          6000
        );
      })
      .unsubscribe();
  }
}

import { Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import {
  CheckoutPaymentMethodComponent,
  CheckoutStepService,
} from '@spartacus/checkout/base/components';
import {
  PaymentDetails,
  UserPaymentService,
  TranslationService,
  GlobalMessageService,
  UserIdService,
  EventService,
} from '@spartacus/core';
import { ActivatedRoute } from '@angular/router';
import { ActiveCartFacade } from '@spartacus/cart/base/root';
import {
  CheckoutDeliveryAddressFacade,
  CheckoutPaymentFacade,
  CheckoutPaymentDetailsCreatedEvent,
  CheckoutQueryReloadEvent,
} from '@spartacus/checkout/base/root';
import { combineLatest, Subscription, Subject, Observable } from 'rxjs';
import { Card } from '@spartacus/storefront';
import { CustomBillingAddressFormComponent } from './custom-billing-address-form/custom-billing-address-form.component';
import { AuthService } from '@spartacus/core';
import { switchMap, takeUntil, filter, map } from 'rxjs/operators';
import { AppState } from 'src/app/store/app-state';
import { OccCheckoutPaymentService } from '../../occ/occ-checkout-payment.service';
import { CustomCheckoutService } from '../../services/custom-checkout.service';

@Component({
  selector: 'atcpps-custom-checkout-payment-method',
  templateUrl: './custom-checkout-payment-method.component.html',
  styleUrls: ['./custom-checkout-payment-method.component.scss'],
})
export class CustomCheckoutPaymentMethodComponent
  extends CheckoutPaymentMethodComponent
  implements OnInit, OnDestroy
{
  override get existingPaymentMethods$(): Observable<PaymentDetails[]> {
    return combineLatest([
      this.userPaymentService.getPaymentMethods(),
      this.checkoutPaymentFacade.getPaymentDetailsState().pipe(
        filter((state) => !state.loading),
        map((state) => state.data)
      ),
    ]).pipe(
      map(([paymentMethods, selectedMethod]) => {
        if (this.appState.getTransactionRetry()) return [];
        const containsSelectedMethod = paymentMethods.find(
          (item: PaymentDetails) => item?.id === selectedMethod?.id
        );
        if (!!selectedMethod && this.isGuestCheckout) {
          return [...paymentMethods, selectedMethod];
        } else if (
          !!selectedMethod &&
          !this.isGuestCheckout &&
          !containsSelectedMethod
        ) {
          return [...paymentMethods, selectedMethod];
        }
        return paymentMethods;
      })
    );
  }

  @ViewChild('customBillingAddressForm')
  billingAddressFormComponent: CustomBillingAddressFormComponent;
  updateBillingSub: Subscription;
  private ngUnsubscribe: Subject<void> = new Subject();
  constructor(
    userPaymentService: UserPaymentService,
    checkoutDeliveryAddressFacade: CheckoutDeliveryAddressFacade,
    checkoutPaymentFacade: CheckoutPaymentFacade,
    activatedRoute: ActivatedRoute,
    translationService: TranslationService,
    activeCartFacade: ActiveCartFacade,
    private authService: AuthService,
    checkoutStepService: CheckoutStepService,
    globalMessageService: GlobalMessageService,
    private occCheckoutPaymentService: OccCheckoutPaymentService,
    private eventService: EventService,
    protected userIdService: UserIdService,
    private _customCheckoutService: CustomCheckoutService,
    private appState: AppState
  ) {
    super(
      userPaymentService,
      checkoutDeliveryAddressFacade,
      checkoutPaymentFacade,
      activatedRoute,
      translationService,
      activeCartFacade,
      checkoutStepService,
      globalMessageService
    );
  }

  override ngOnInit(): void {
    super.ngOnInit();
  }

  override ngOnDestroy() {
    super.ngOnDestroy();
    if (this.updateBillingSub) this.updateBillingSub.unsubscribe();
    this.ngUnsubscribe.next();
    this.ngUnsubscribe.complete();
  }

  public handleContinue(event: Event): void {
    event.stopImmediatePropagation();
    event.preventDefault();
    this.billingAddressFormComponent.billingAddressForm.markAllAsTouched();
    if (
      !this.billingAddressFormComponent.sameAsDeliveryAddress &&
      !this.billingAddressFormComponent.billingAddressForm.valid
    )
      return;
    const billingAddress = this.billingAddressFormComponent
      .sameAsDeliveryAddress
      ? this.deliveryAddress
      : this.billingAddressFormComponent.billingAddressForm.value;
    const paymentDetails: PaymentDetails = {};
    const { email, ...restOfAddress } = billingAddress;
    paymentDetails.billingAddress = email ? billingAddress : restOfAddress;
    this.busy$.next(true);
    this.updateBillingSub = combineLatest([
      this.authService.isUserLoggedIn(),
      this.activeCartFacade.getActiveCartId(),
    ])
      .pipe(
        takeUntil(this.ngUnsubscribe),
        switchMap(([loginStatus, cartId]) => {
          return this.occCheckoutPaymentService.updateBillingAddress(
            loginStatus,
            cartId,
            paymentDetails
          );
        })
      )
      .subscribe(() => {
        this.updatePaymentChangeEvent();
      })
      .add(() => {
        this.busy$.next(false);
      });
  }

  addedNewCard(data: any): void {
    this.paymentDetails = data.paymentDetails;
    this.updateCheckoutEvent(data.paymentDetails);
    this.newPaymentFormManuallyOpened = false;
    this.appState.setTransactionRetry(false);
  }

  private updatePaymentChangeEvent(): void {
    this.eventService.dispatch({}, CheckoutQueryReloadEvent);
    this.next();
  }

  private updateCheckoutEvent(paymentDetails: PaymentDetails): void {
    this.subscriptions.add(
      combineLatest([
        this.userIdService.takeUserId(),
        this.activeCartFacade.takeActiveCartId(),
      ]).subscribe(([userId, cartId]) => {
        this.eventService.dispatch(
          {
            userId,
            cartId,
            paymentDetails,
          },
          CheckoutPaymentDetailsCreatedEvent
        );
      })
    );
  }

  override createCard(
    paymentDetails: PaymentDetails,
    cardLabels: {
      textDefaultPaymentMethod: string;
      textExpires: string;
      textUseThisPayment: string;
      textSelected: string;
    },
    selected: PaymentDetails | undefined
  ): Card {
    return {
      role: 'region',
      title: paymentDetails.defaultPayment
        ? cardLabels.textDefaultPaymentMethod
        : '',
      textBold: paymentDetails.accountHolderName,
      text: [
        this._customCheckoutService.maskCreditCardNumber(
          paymentDetails.cardNumber ?? ''
        ),
        cardLabels.textExpires,
      ],
      img: this.getCardIcon(paymentDetails.cardType?.code as string),
      actions: [{ name: cardLabels.textUseThisPayment, event: 'send' }],
      header:
        selected?.subscriptionId === paymentDetails.subscriptionId
          ? cardLabels.textSelected
          : undefined,
      label: paymentDetails.defaultPayment
        ? 'paymentCard.defaultPaymentLabel'
        : 'paymentCard.additionalPaymentLabel',
    };
  }
}

import {
  Component,
  ViewContainerRef,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  OnDestroy,
  ViewChild,
  OnInit,
} from '@angular/core';
import { CheckoutPlaceOrderComponent } from '@spartacus/checkout/base/components';
import { LAUNCH_CALLER, LaunchDialogService } from '@spartacus/storefront';
import { OrderFacade } from '@spartacus/order/root';
import {
  RoutingService,
  UserIdService,
} from '@spartacus/core';
import {
  FormArray,
  FormControl,
  UntypedFormBuilder,
  UntypedFormGroup,
  Validators,
} from '@angular/forms';
import { CmsService } from '@spartacus/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import {
  ActiveCartFacade,
  Cart,
} from '@spartacus/cart/base/root';
import { combineLatest, Subscription, Subject } from 'rxjs';
import { switchMap, takeUntil, first } from 'rxjs/operators';
import { BrowserStorageService } from 'src/app/store/browser-storage.service';
import { StoreKeys } from 'src/app/store/store-keys';
import { OCC_USER_ID_ANONYMOUS } from '@spartacus/core';
import { OccCheckoutPlaceOrderService } from '../../occ/occ-checkout-place-order.service';
import { CustomCheckout3DSPaymentsComponent } from '../custom-checkout-3ds/custom-checkout-3ds-payments/custom-checkout-3ds-payments.component';

@Component({
  selector: 'atcpps-checkout-place-order',
  templateUrl: './custom-checkout-place-order.component.html',
  styleUrls: ['./custom-checkout-place-order.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomCheckoutPlaceOrderComponent
  extends CheckoutPlaceOrderComponent
  implements OnInit, OnDestroy
{
  form: FormGroup;
  userId: any = '';
  pageId: any = '';
  consentData$: any;
  data: any[] = [];
  public errorMsg = '';
  postSubscriptionConsent: Subscription | undefined;
  onSubmitSubscription: Subscription | undefined;
  checkingLength: number | undefined;
  placeOrderConsent: Subscription | undefined;
  checkingsub: Subscription | undefined;
  private ngUnsubscribe: Subject<void> = new Subject();
  paymentData: any;

  @ViewChild(CustomCheckout3DSPaymentsComponent) checkout3DSPayments: CustomCheckout3DSPaymentsComponent;

  constructor(
    orderFacade: OrderFacade,
    routingService: RoutingService,
    fb: UntypedFormBuilder,
    launchDialogService: LaunchDialogService,
    vcr: ViewContainerRef,
    private cd: ChangeDetectorRef,
    protected userIdService: UserIdService,
    private _occCheckoutPlaceOrderService: OccCheckoutPlaceOrderService,
    protected cms: CmsService,
    private formBuilder: FormBuilder,
    private activeCartFacade: ActiveCartFacade,
    private _browserStorageService: BrowserStorageService,
  ) {
    super(orderFacade, routingService, fb, launchDialogService, vcr);
    this.form = this.formBuilder.group({
      checkArray: this.formBuilder.array([], [Validators.required]),
    });
  }
  override checkoutSubmitForm: UntypedFormGroup = this.fb.group({
    termsAndConditions: [true],
  });
  get consentFormArray() {
    return this.form.controls['checkArray'] as FormArray;
  }

  onConsentChange(e: any) {
    // Fix me
    e.preventDefault();
    const checkArray: FormArray = this.consentFormArray;
    if (e.target.checked) {
      checkArray.push(new FormControl(e.target.value));
    } else {
      let ct = 0;
      checkArray.controls.forEach((item: any) => {
        if (item.value == e.target.value) {
          checkArray.removeAt(ct);
          return;
        }
        ct++;
      });
    }
  }

  enablePlaceHolder() {
    this.checkingsub = this.consentData$.subscribe((len: any) => {
      return (this.checkingLength = len?.consentTemplates.length);
    });
    if (this.consentFormArray.length === this.checkingLength) {
      return false;
    } else {
      return true;
    }
  }

  ngOnInit(): void {
    this.consentData$ = this.loadConsent();
  }

  loadConsent(): any {
    return combineLatest([
      this.userIdService.takeUserId(),
      this.cms.getCurrentPage(),
    ]).pipe(
      first(),
      takeUntil(this.ngUnsubscribe),
      switchMap(([userid, data]) => {
        this.userId = userid;
        this.pageId = data?.pageId;
        return this._occCheckoutPlaceOrderService.getConsent(
          this.userId,
          this.pageId
        );
      })
    );
  }

  giveConsent(consent: any, cartId: any) {
    consent?.consentTemplates?.forEach((data: any) => {
      const obj = {
        id: data.id,
        version: data.version,
      };
      this.data.push(obj);
    });
    const checkedDetails = {
      cartId: cartId,
      consentTemplates: this.data,
    };
    return checkedDetails;
  }

  onSubmitOrder(event: Event) {
    event.preventDefault();
    this.showLoaderSpinner();
    if (!this.paymentData) {
      this.checkout3DSPayments.enablePlaceOrderButton(false);
      this.onSubmitSubscription = this.activeCartFacade
        .getActive()
        .subscribe((cart) => {
          this.initiateOrderSubmission(cart);
        });
    } else {
      this.checkout3DSPayments.initiate3DSFlow(this.paymentData, true);
    }
  }

  private initiateOrderSubmission(cart: Cart) {
    this.postSubscriptionConsent = combineLatest([
      this.activeCartFacade.getActive(),
      this.loadConsent(),
    ])
      .pipe(
        first(),
        takeUntil(this.ngUnsubscribe),
      switchMap(([cart, consent]) => {
        const reqDetails = this.giveConsent(consent, cart?.code);
          return this._occCheckoutPlaceOrderService.postConsent(reqDetails);
        })
      )
      .subscribe((data) => {
        const details = data.body;
        if(details.isValid){
          const cartId = this.getCartIdByUser(this.userId, cart);
          this.checkout3DSPayments.verify3DSPayments(this.userId, cartId)
        }
        if(details.errorMessage && !details.isValid){
          this.checkout3DSPayments.showErrorMessage(data.body.errorMessage);
          this.checkout3DSPayments.resetOrderSubmission(false);
        }
    });
  }

  public submitOrder() {
    this.activeCartFacade.isGuestCart().subscribe((isGuest) => {
      if (isGuest)
        this._browserStorageService.deleteItem(StoreKeys.SameAsDeliveryAddress);
    });
    this.checkout3DSPayments.resetOrderSubmission(false);
    this.submitForm();
  }

  override submitForm(): void {
    this.errorMsg = '';
    this.orderFacade.clearPlacedOrder();
    this.placeOrderConsent = this.orderFacade
      .placeOrder(this.checkoutSubmitForm.valid)
      .subscribe({
        error: (data) => {
          this.errorMsg = data?.details[0]?.type;
          this.cd.detectChanges();
        if (!this.placedOrder) { return; }
        this.checkout3DSPayments.resetOrderSubmission(true);
        },
        next: (response) => {
          if (response && response.code)
            sessionStorage.setItem('orderId', response.code);
          this.onSuccess();
        },
      });
  }

  private getCartIdByUser(userId: string, cart: Cart): string {
    if (userId === OCC_USER_ID_ANONYMOUS) {
      return cart?.guid ?? '';
    }
    return cart?.code ?? '';
  }

  public setPaymentData(data: any) {
    this.paymentData = data;
  }

  private showLoaderSpinner() {
    this.placedOrder = this.launchDialogService.launch(
      LAUNCH_CALLER.PLACE_ORDER_SPINNER,
      this.vcr
    );
  }

  override ngOnDestroy(): void {
    super.ngOnDestroy();
    if (this.postSubscriptionConsent)
      this.postSubscriptionConsent.unsubscribe();
    if (this.placeOrderConsent) this.placeOrderConsent.unsubscribe();
    if (this.checkingsub) this.checkingsub.unsubscribe();
    this.ngUnsubscribe.next();
    this.ngUnsubscribe.complete();
  }
}

import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { OccEndpointsService } from '@spartacus/core';
import { Observable } from 'rxjs';

@Injectable()
export class OccCheckoutEmailAdapter {
  constructor(
    private _http: HttpClient,
    private _occEndpointsService: OccEndpointsService
  ) {}
  updateEmailToCart(
    userId: string,
    cartId: string,
    email: string
  ): Observable<{}> {
    const httpParams: HttpParams = new HttpParams().set('email', email);

    const url = this._occEndpointsService.buildUrl('updateEmail', {
