}

import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { OccEndpointsService } from '@spartacus/core';
import { Observable, combineLatest, throwError } from 'rxjs';
import { catchError, map, switchMap, take } from 'rxjs/operators';
import { UserIdService } from '@spartacus/core';
import { ActiveCartService } from '@spartacus/cart/base/core';
import { OCC_USER_ID_ANONYMOUS } from '@spartacus/core';
import { DSDAppointment } from '../../appointment-booking/models/appointment-booking.models';
import { GlobalMessageService } from '@spartacus/core';
import { GlobalMessageType } from '@spartacus/core';

@Injectable({
  providedIn: 'root',
})
export class OccCheckoutDeliveryModeService {
  constructor(
    private _activeCartService: ActiveCartService,
    private http: HttpClient,
    private occEndpoints: OccEndpointsService,
    private _userIdService: UserIdService,
    private _globalMsgService: GlobalMessageService
  ) {}

  public saveAppointmentToCart(appointment: DSDAppointment): Observable<any> {
    const appointmentPayload = {
      appointment: appointment,
    };
    return this._getUserIdAndCartId().pipe(
      switchMap(([userId, cartId]) => {
        return this.http
          .post(
            this.occEndpoints.buildUrl('storeAppointmentOnCart', {
              urlParams: { userId, cartId },
            }),
            appointmentPayload
          )
          .pipe(
            catchError((response: any) => {
              if (response instanceof HttpErrorResponse) {
                this.handleDABErrors(response);
              }
              return throwError(response);
            })
          );
      })
    );
  }

  private handleDABErrors(errorResponse: HttpErrorResponse) {
    const message =
      errorResponse.status === 404
        ? { key: 'httpHandlers.dsdSystemUnavailable' }
        : errorResponse?.error?.errors[0]?.message;
    this._globalMsgService.add(message, GlobalMessageType.MSG_TYPE_ERROR, 2000);
  }

  protected _getUserIdAndCartId(): Observable<[string, string]> {
    return combineLatest([
      this._userIdService.takeUserId(),
      this._activeCartService.takeActiveCartId(),
      this._activeCartService.isGuestCart(),
    ]).pipe(
      take(1),
      map(([userId, cartId, isGuestCart]) => {
        if (
          !userId ||
          !cartId ||
          (userId === OCC_USER_ID_ANONYMOUS && !isGuestCart)
        ) {
          throw new Error('Checkout conditions not met');
        }
        return [userId, cartId];
      })
    );
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { OccEndpointsService } from '@spartacus/core';
import { Observable } from 'rxjs';
import { OccCheckoutConfig } from './occ-endpoints-config';

@Injectable({
  providedIn: 'root'
})
export class OccCheckoutPaymentService {

  constructor(private http: HttpClient, private occEndpoints: OccEndpointsService) { }

  public getMpayToken(isUserLoggedIn: boolean, cartId: string): Observable<any> {
    return this.http.post(this.buildUrl('mpayToken', isUserLoggedIn, cartId), {})
  }

  public updatePaymentDetails(isUserLoggedIn: boolean | undefined, cartId: string | undefined, asset: any): Observable<any> {
    return this.http.post(this.buildUrl('updatePayment', isUserLoggedIn, cartId), asset, {})
  }

  private buildUrl(context: string, isLoggedIn: boolean | undefined, cartId: string | undefined): string {
    const occBaseurl = this.occEndpoints.getBaseUrl();
    const cartType = isLoggedIn ? OccCheckoutConfig.userCart : OccCheckoutConfig.guestCart;
    const endpoint = OccCheckoutConfig[context as keyof typeof OccCheckoutConfig];
    return `${occBaseurl}${cartType}${cartId}${endpoint}`;
  }

  public updateBillingAddress(isUserLoggedIn: boolean | undefined, cartId: string | undefined,payload:any):Observable<any>{
    const formData = new FormData();
    Object.keys(payload.billingAddress).forEach(key => {
      if(key==='country'){
        return formData.append('billTo_country', payload.billingAddress.country['isocode'])
      }else{
        return formData.append(`billTo_${key}`, payload.billingAddress[key])
      }

    });
    return this.http.post(this.buildUrl('updatebillingaddress', isUserLoggedIn, cartId),formData,  {})
  }
}

import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { OccEndpointsService } from "@spartacus/core";
import { Observable } from "rxjs";
import { OccCheckoutConfig } from "./occ-endpoints-config";
import { OCC_USER_ID_ANONYMOUS } from "@spartacus/core";
import { USE_CLIENT_TOKEN } from "@spartacus/core";
import { InterceptorUtil } from "@spartacus/core";

export interface ConsentBody {
  cartId: string,
  consentTemplates: Array<ConsentTemplates>
}
export interface ConsentTemplates {
  id: string,
  version: number,
}
@Injectable({
  providedIn: 'root'
})

export class OccCheckoutPlaceOrderService {
  constructor(
    private http: HttpClient, 
    private occEndpoints: OccEndpointsService
  ) { }

  private getHeadersByUserId(userId: string) {
    let headers = new HttpHeaders({
      'Content-Type': 'application/x-www-form-urlencoded',
    });
    if (userId === OCC_USER_ID_ANONYMOUS) {
      headers = InterceptorUtil.createHeader(USE_CLIENT_TOKEN, true, headers);
    }
    return headers;
  }

  private postConsentbuildUrl(context: string) {
    const occBaseurl = this.occEndpoints.getBaseUrl();
    const endpoint = OccCheckoutConfig[context as keyof typeof OccCheckoutConfig];
    return `${occBaseurl}${endpoint}`
    }

  private buildUrl(context: string, userId: string | undefined, pageId: string | undefined): string {
    const occBaseurl = this.occEndpoints.getBaseUrl();
    const endpoint = '/users/' + userId + '/consenttemplates/pages?pageId=';
    return `${occBaseurl}${endpoint}${pageId}`;
  }

  private postDeviceInformationUrl(context: string, userId: string, cartId: string, deviceInformation: string): string {
    const path = '/users/' + userId + '/orders/preauth';
    return this.occEndpoints.buildUrl(path, { queryParams: { cartId, deviceInformation } });
  }

  private getTransactionStatusUrl(context: string, userId: string, transactionId: string) {
    const path = '/users/' + userId + '/orders/status';
    return this.occEndpoints.buildUrl(path, { queryParams: { transactionId } });
  }

  public getConsent(userId: string, pageId: any): Observable<any> {
    return this.http.get(this.buildUrl('getConsent', userId, pageId), {});
  }

  public postConsent(body:ConsentBody): Observable<any> {
    return this.http.post(this.postConsentbuildUrl('postConsent'), body, {observe: 'response'});
  }

  public postDeviceInformation(userId: string, cartId: string, deviceInformation: string) {
    const headers = this.getHeadersByUserId(userId);
    return this.http.post(this.postDeviceInformationUrl('auth', userId, cartId, deviceInformation), {}, { headers });
  }

  public getTransactionStatus(userId: string, transactionId: string) {
    const headers = this.getHeadersByUserId(userId);
    return this.http.post(this.getTransactionStatusUrl('auth', userId, transactionId), {}, { headers });
  }
}

import { Injectable } from '@angular/core';
import { ActiveCartFacade } from '@spartacus/cart/base/root';
import { UserAddressService } from '@spartacus/core';
import { Address } from '@spartacus/core';
import { getLastValueSync } from '@spartacus/core';
import { Observable, combineLatest } from 'rxjs';
import { OccCheckoutEmailAdapter } from '../occ/adapters/occ-checkout-email-adapter';
import { UserIdService } from '@spartacus/core';
import { map, switchMap, take } from 'rxjs/operators';
import { AuthService } from '@spartacus/core';
import { UtilService } from 'src/app/utilities/util.service';
import { countryConfig } from 'src/app/countries/country-config';

@Injectable({
  providedIn: 'root',
})
export class CustomCheckoutService {
  constructor(
    protected activeCartFacade: ActiveCartFacade,
    private _authService: AuthService,
    protected userAddressService: UserAddressService,
    private _occCheckoutEmailAdapter: OccCheckoutEmailAdapter,
    private _userIdService: UserIdService,
    private _utilService: UtilService
  ) {}

  public maskCreditCardNumber(lastDigits: string) {
    const maxLength = 16 + 3;
    const maskedNumber =
      '**** **** **** ' +
      (lastDigits.length > 4
        ? lastDigits.slice(-4)
        : lastDigits.padStart(4, '*'));
    return maskedNumber.slice(0, maxLength);
  }

  isGuestCheckout(): boolean {
    return !!getLastValueSync(this.activeCartFacade.isGuestCart());
  }

  getSavedUserAddress(): Observable<Address[]> {
    return this.userAddressService.getAddresses();
  }

  loadAddresses(): void {
    if (!this.isGuestCheckout) {
      this.userAddressService.loadAddresses();
    }
  }

  isEmailUpdateRequired(): Observable<boolean> {
    return combineLatest([
      this._authService.isUserLoggedIn(),
      this.activeCartFacade.getActive(),
    ]).pipe(
      map(
        ([isUserLoggedIn, activeCart]) =>
          isUserLoggedIn && !activeCart?.emailAvailable
      )
    );
  }

  updateEmailToCart(email: string): Observable<any> {
    return combineLatest([
      this._userIdService.takeUserId(),
      this.activeCartFacade.takeActiveCartId(),
    ]).pipe(
      take(1),
      switchMap(([userId, cartId]) => {
        return this._occCheckoutEmailAdapter.updateEmailToCart(
          userId,
          cartId,
          email
        );
      })
    );
  }

  getMpayFormLanguage(): string {
    let mpayFormLanguage = this.getMpayCountrySpecificLanguage();
    if (!mpayFormLanguage) {
      mpayFormLanguage = this._utilService.getSiteContext('language');
    }
    return mpayFormLanguage;
  }

  getMpayCountrySpecificLanguage(): string {
    const activeCountryIsoCode = this._utilService.getActiveCountryIsoCode();
    const currentConfig = countryConfig.find(obj => obj.countryCode === activeCountryIsoCode);
    return currentConfig?.mpayCountrySpecificLanguage || '';
  }
}

