import { Component, Input, OnInit, ViewChild, ElementRef, OnDestroy, HostListener, Output, EventEmitter } from '@angular/core';
import { CmsBannerComponent } from '@spartacus/core/src/model';
import { BannerComponent } from '@spartacus/storefront';
import { timer } from 'rxjs';
interface CMSBannerComponentCustom extends CmsBannerComponent {
  theme?: string;
  animationScale?: number;
  animationSpeed?: number;
}

@Component({
  selector: 'atcpps-full-width-slider-item',
  templateUrl: './full-width-slider-item.component.html',
  styleUrls: ['./full-width-slider-item.component.scss']
})
export class FullWidthSliderItemComponent extends BannerComponent implements OnInit, OnDestroy {
  @ViewChild('bannerImage') bannerImage: ElementRef<HTMLElement>;
  @Output() hideParentLoader = new EventEmitter<boolean>();
  animationScale: number;
  theme: string;
  subscription: any;
  public showLoader = true;
  @Input() item: CMSBannerComponentCustom;
  ngOnInit(): void {
    this.setRouterLink(this.item);
    this.subscription = this.data$.subscribe((data: any) => {
      this.animationScale = parseInt(data?.animationScale);
      this.theme = data?.effect;
      (this.theme && this.theme != 'DARK' && this.theme != 'LIGHT') && (this.theme = 'DARK')
    })
  }
  // following section is added to prevent the display issue which is occuring if the
  // viewport quickly switched from responsive mode to desktop view
  @HostListener('window:resize', ['$event'])
  onResize() {
    timer(100).subscribe(() => this.adjustContenAndShadowRootStyle());
  }
  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }
  private adjustContenAndShadowRootStyle() {
    // This code block is required to set the position of banner content as
    // the position of the banner cannot be made as 'absolute' so there isno way
    // to provide the position as percentage only calculative position can be given
    const bannerContent: any = (this.bannerImage.nativeElement.getElementsByClassName('banner-content'))[0];
    const bannerImage: HTMLImageElement = (this.bannerImage.nativeElement.getElementsByTagName('img'))[0];
    const applyStyle = (bannerImage: HTMLImageElement) => {
      this.stopLoader();
      if (bannerContent) {
        bannerContent.style.bottom = (bannerImage.height / 1.5) + "px";
        bannerContent.style.display = 'block';
      }
    }
    // checking if the image is actually loaded in DOM tree or not
    if (bannerImage.clientHeight == 0) {
      // image is not loaded yet to dom tree applying a bit more delay
      timer(100).subscribe(() => {
        applyStyle(bannerImage);
      });
    } else {
      applyStyle(bannerImage);
    }
  }
  applyScalingAnimation(): void {
    const bannerImage: HTMLImageElement = (this.bannerImage.nativeElement.getElementsByTagName('img'))[0];
    // Following timer is added to start the animation after a fixed delay as per mention in ATCMBRDI-109
    timer(100).subscribe(() => {
      bannerImage.setAttribute('style',
        `
      width: 100%;
      transform: scale(${(1 + (this.animationScale / 100))});
      transition: all 8000ms ease-in-out;
    `);
      this.adjustContenAndShadowRootStyle();
    });
  }
  stopLoader() {
    this.showLoader = false;
    this.hideParentLoader.emit(false);
  }
}

