import { ChangeDetectionStrategy, ChangeDetectorRef, Component, OnInit } from '@angular/core';
import {
  UntypedFormBuilder,
  UntypedFormGroup,
  Validators,
} from '@angular/forms';
import { RoutingService, GlobalMessageService, GlobalMessageType } from '@spartacus/core';
import { CustomOrderReturnService } from '../../../services/custom-order-return.service';
import { catchError, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'atcpps-custom-guest-return',
  templateUrl: './custom-guest-return.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomGuestReturnComponent implements OnInit {
  email: string;
  orderId: string;
  isGuest = false;
  apiSuccess = false;
  errorType = '';
  guestOrderReturnForm: UntypedFormGroup;

  constructor(
    protected formBuilder: UntypedFormBuilder,
    private _routing: RoutingService,
    private _customOrderReturnService: CustomOrderReturnService,
    private cdr: ChangeDetectorRef,
    private route: ActivatedRoute,
    private globalMessageService: GlobalMessageService,
  ) { 
  }
  ngOnInit() {
    this.orderId = this.route.snapshot?.queryParams['orderCode'];
    this.email = this.route.snapshot?.queryParams['email'];
    this.guestOrderReturnForm = this.formBuilder.group({
      orderId: [this.orderId, [Validators.required]],
      email: [this.email, [Validators.required, Validators.email]],
    });
    if(this.orderId && this.email) {
      this.validateOrder(this.orderId, this.email);
    }
  }

validateOrder(orderId:string, email:string) {
 this._customOrderReturnService.verifyReturnOrder(orderId, email).pipe(
    tap((verificationResult: any) => {

      if (verificationResult.guid) {
        this._customOrderReturnService.isGuest = true;
        this._customOrderReturnService.setIncludeCustomAuthGuard(this._customOrderReturnService.isGuest);
        this._customOrderReturnService.storeGuid(
          verificationResult.user,
          verificationResult.guid
        );
        this._customOrderReturnService.setIncludeGuestReturnOrder(orderId, email);
        this.apiSuccess = !this.apiSuccess;
        this.cdr.detectChanges();
      }

    }),
    catchError((error) => {
      if (error) {
        this.errorType = error?.error?.errors[0]?.subject;
        this.cdr.detectChanges();
        this.globalMessageService.remove(GlobalMessageType.MSG_TYPE_ERROR);
      }
     return throwError(error);
    })
  ).subscribe();
}

  onSubmit() {
    if (this.guestOrderReturnForm.valid) {
    const orderId =  this.guestOrderReturnForm.get('orderId')?.value;
     const email = this.guestOrderReturnForm.get('email')?.value;

    if(this.orderId === undefined && this.email === undefined) {
        this.validateOrder(orderId, email);
      }
    if(this.apiSuccess){
      const code = orderId;
      this._routing.go({
        cxRoute: 'guestReturnOrder',
        params: { code },
      });
    }

      if(this.errorType === 'REGISTERED_USER_ORDER') {
        const code = orderId;
        this._routing.go({
          cxRoute: 'orderReturn',
         params: { code },
        });
      }
    }
}
clearErrors() {
  this.errorType = '';
}
}

import { DatePipe } from '@angular/common';
import { Component } from '@angular/core';
import { OrderReturnRequestFacade } from '@spartacus/order/root';
import { map } from 'rxjs/operators';
import { RoutingService } from '@spartacus/core';

@Component({
  selector: 'atcpps-custom-return-order-confirmation',
  templateUrl: './custom-return-order-confirmation.component.html',
  providers: [DatePipe]
})
export class CustomReturnOrderConfirmationComponent {
  return$:any
  currentDate: Date = new Date();
  returnStatus: string;

  constructor(protected returnRequestService: OrderReturnRequestFacade,
    private datePipe: DatePipe,
    protected routingService: RoutingService,) {
      this.currentDate = new Date();
    }

    capitalizeWords(text: string): string {
      return text.toLowerCase().replace(/(^|\s)\S/g, (match) => match.toUpperCase());
    }

  ngOnInit() {
  this.return$ = this.returnRequestService
      .getOrderReturnRequest().pipe(
        map((returnRequest:any) => {
          this.returnStatus = this.capitalizeWords(returnRequest.status.replace(/_/g, ' '))
          return returnRequest;
        })
      )
  }

  formattedDate(): any {
    return this.datePipe.transform(this.currentDate, 'MMMM dd, yyyy');
  }

  back(): void {
    this.routingService.go(
      { cxRoute: 'orders' },
      {
        state: {
          activeTab: 1,
        },
      }
    );
  }
}

import { Store } from '@ngrx/store';
import { RoutingService, GlobalMessageService } from '@spartacus/core';
import { Injectable } from '@angular/core';
import { GlobalMessageType } from '@spartacus/core';
import { OrderDetailsService, OrderReturnService } from '@spartacus/order/components';
import { OrderActions, StateWithOrder } from '@spartacus/order/core';
import { CancelOrReturnRequestEntryInput, OrderReturnRequestFacade, ReturnRequestEntryInputList } from '@spartacus/order/root';
import { BehaviorSubject, Observable } from 'rxjs';
import { first, map } from 'rxjs/operators';
import { OccGuestReturnOrderAdapter } from '../occ/adapter/occ-guest-order-return-adapter';
import { GuestOrderReturn, userData } from '../../product/models/mb.model';

@Injectable({
  providedIn: 'root',
})
export class CustomOrderReturnService extends OrderReturnService {

  isGuest = false;
  guestOrderCode:GuestOrderReturn;
  guestGuid: string;

  private dataSubject = new BehaviorSubject<any>({ data: '', guid: '' });
  public data$: Observable<userData> = this.dataSubject.asObservable();


  storeGuid(data: userData, guid:string) {
    this.dataSubject?.next({data, guid});
  }

  private includeGuestReturnOrder = new BehaviorSubject<GuestOrderReturn> ({orderId: '', email: ''})
  public includeGuestReturnOrder$: Observable<GuestOrderReturn> = this.includeGuestReturnOrder.asObservable();

  setIncludeGuestReturnOrder(orderId: string, email: string):void {
    this.includeGuestReturnOrder?.next({orderId, email});
  }

  private _includeCustomAuthGuard = new BehaviorSubject<boolean>(false);
  includePocCustomGuard$ = this._includeCustomAuthGuard.asObservable();

  setIncludeCustomAuthGuard(value: boolean): void {
    this._includeCustomAuthGuard?.next(value);
  }

  constructor(
    private _occGuestReturnOrderAdapter: OccGuestReturnOrderAdapter,
    protected override orderDetailsService: OrderDetailsService,
    protected override returnRequestService: OrderReturnRequestFacade,
    protected override routing: RoutingService,
    protected override globalMessageService: GlobalMessageService,
    protected store: Store<StateWithOrder>,
  ) {
    super(orderDetailsService, returnRequestService, routing, globalMessageService);
  }

  override save(): void {
this.includeGuestReturnOrder$
.pipe(
  map((orderId: GuestOrderReturn) => orderId?.orderId)
)
.subscribe((orderId: any) => {
  this.guestOrderCode = orderId;
});
    const orderCode = this.isGuest ? this.guestOrderCode : this.form.value.orderCode;
    const entries = this.form.value.entries;
    const inputs: CancelOrReturnRequestEntryInput[] = Object.keys(entries)
      .filter((entryNumber) => <number>entries[entryNumber] > 0)
      .map(
        (entryNumber) =>
          ({
            orderEntryNumber: Number(entryNumber),
            quantity: <number>entries[entryNumber],
          } as CancelOrReturnRequestEntryInput)
      );

    this.form.reset();

    if(this.isGuest) {
      this.createGuestOrderReturnRequest({
        orderCode,
        returnRequestEntryInputs: inputs,
      });
    } else {
      this.returnRequestService.createOrderReturnRequest({
        orderCode,
        returnRequestEntryInputs: inputs,
      })
    }

    this.returnRequestService
      .getReturnRequestSuccess()
      .pipe(first(Boolean))
      .subscribe(() => this.afterSaves());
  }

  createGuestOrderReturnRequest(returnRequestInput: ReturnRequestEntryInputList) {
    this.data$
  .pipe(
    map((uid: userData) => uid?.data?.uid)
  )
  .subscribe((id: any) => {
    this.guestGuid = id.replace(/\|/g, '%7c');
  });
       const userId = this.guestGuid;
        this.store.dispatch(
          new OrderActions.CreateOrderReturnRequest({
            userId,
            returnRequestInput,
          })
        );
      }

  afterSaves() {
    this.returnRequestService
      .getOrderReturnRequest()
      .pipe(first((r) => !!r))
      .subscribe((returnRequest) => {
        const rma = returnRequest.rma;
        this.globalMessageService.add(
          {
            key: 'orderDetails.cancellationAndReturn.returnSuccess',
            params: { rma },
          },
          GlobalMessageType.MSG_TYPE_CONFIRMATION
        );
        this.routing.go({
          cxRoute: 'returnsubmitted',
          params: { rma },
        });
      });
  }

  verifyReturnOrder(orderId:string, emailId:string) {
    return this._occGuestReturnOrderAdapter.verifyGuestReturnOrder(orderId, emailId);
  }

  guestOrderDetails(userId: string, orderCode: string) {
    return this._occGuestReturnOrderAdapter.guestOrderDetails(userId, orderCode)
  }
}

