    super(http, occEndpoints, converter);
  }

  override load(pageContext: PageContext): Observable<CmsStructureModel> {
    const params = this.getPagesRequestParams(pageContext);
    if (PageParamsConfig.cart.includes(params.pageLabelOrId as string)) {
      return this.activeCartService.getActiveCartId().pipe(
        take(1),
        switchMap(cartId => {
          if (cartId) {
            this._browserStorageService.storeItem(StoreKeys.cartId, cartId)
            this.endpoint = this._addContextToEndpoint(pageContext, params, cartId);
          } else {
            this.endpoint = this._addContextToEndpoint(pageContext, params, this._browserStorageService.readItem(StoreKeys.cartId) as string);
            this._browserStorageService.deleteItem(StoreKeys.cartId);
          }
          return this.http
            .get(this.endpoint, { headers: this.headers })
            .pipe(this.converter.pipeable(CMS_PAGE_NORMALIZER));
        })
      );
    }
    else if (PageParamsConfig.order.includes(params.pageLabelOrId as string)) {
      const orderId: string = sessionStorage.getItem('orderId') as string;
      sessionStorage.removeItem('orderId');
      const endpoint = this._addContextToEndpoint(pageContext, params, orderId);
      return this.http
        .get(endpoint, { headers: this.headers })
        .pipe(this.converter.pipeable(CMS_PAGE_NORMALIZER));
    } else {
      const endpoint = this._addContextToEndpoint(pageContext, params);
      return this.http
        .get(endpoint, { headers: this.headers })
        .pipe(this.converter.pipeable(CMS_PAGE_NORMALIZER));
    }
  }


  private _addContextToEndpoint(pageContext: PageContext, params: any, contextId?: string): string {
    if (contextId) params.code = contextId;
    const endpoint = !pageContext.type
      ? this.occEndpoints.buildUrl('page', {
        urlParams: { id: pageContext.id },
      })
      : this.occEndpoints.buildUrl('pages', { queryParams: params });

    return endpoint;
  }
}

import { Injectable } from "@angular/core";
import { ConverterService } from "@spartacus/core";
import { PRODUCT_NORMALIZER } from "@spartacus/core";
import { OccProductSearchPageNormalizer } from "@spartacus/core";
import { Converter, Occ, ProductSearchPage } from "@spartacus/core";


@Injectable({
    providedIn: 'root'
  })
export class CustomOccProductSearchPageNormalizer extends OccProductSearchPageNormalizer
  implements Converter<Occ.ProductSearchPage, ProductSearchPage>{

  constructor(
    converterService: ConverterService,
    public _converterService: ConverterService ) {
      super(converterService)
  }

  override convert(
    source: Occ.ProductSearchPage,
    target: ProductSearchPage = {}
  ): ProductSearchPage {
    target = {
      ...target,
      ...(source as any),
    };

    this.normalizeFacet(target);
    if (source.products) {
      target.products = source.products.map((product) =>
        this._converterService.convert(product, PRODUCT_NORMALIZER)
      );
    }
    return target;
  }

  private normalizeFacet(target: ProductSearchPage): void {
    this.normalizeFacetValues(target);
  }
}

