import { CarouselComponent } from '@spartacus/storefront';
import { InViewportAction } from 'ng-in-viewport';
import { Subscription, zip } from 'rxjs';
import { CustomViewCarouselListService } from 'src/app/tracking/events/services/custom-view-carousel-list-event.service';
import { UtilService } from 'src/app/utilities/util.service';
@Component({
  selector: 'cx-carousel',
  templateUrl: './carousel.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomCarouselComponent
  extends CarouselComponent
  implements OnInit, OnDestroy
{
  private subscription = new Subscription();
  private loadedItems: any[] = [];
  private seenProducts: { [productCode: string]: boolean } = {};
  private visited = false;
  private position: number;

  constructor(
    _el: ElementRef,
    _service: CarouselService,
    private utilService: UtilService,
    private _winRef: WindowRef,
    private _customViewCarouselListService: CustomViewCarouselListService
  ) {
    super(_el, _service);
  }

  override ngOnInit(): void {
    super.ngOnInit();
    this.subscription.add(
      zip(...this.items).subscribe((results: any) => {
        this.loadedItems = results;
      })
    );
    this.position =
      this._winRef.document.getElementsByTagName('cx-carousel')?.length;
  }

  onIntersection(event: InViewportAction, size: number) {
    if (event.visible && !this.visited) {
      this.visited = true;
      this.dispatchViewItemListEvent(
        this.loadedItems.slice(this.activeSlide, this.activeSlide + size)
      );
    }
  }

  showNextSlide(activeSlide: number, size: number) {
    const newProducts: any = [];
    this.loadedItems
      .slice(activeSlide, activeSlide + size)
      .forEach((item: any) => {
        const productCode = item.code;
        if (!this.seenProducts[productCode]) {
          this.seenProducts[productCode] = true;
          newProducts.push(item);
        }
      });

    if (newProducts.length) this.dispatchViewItemListEvent(newProducts);
  }

  private dispatchViewItemListEvent(products: Product[]) {
    const title = this.utilService.toSnakeCase(this.title ? this.title : '');
    this._customViewCarouselListService.dispatchCustomViewItemListEvent(
      products,
      title,
      this.position,
      this.loadedItems
    );
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
  }
}

