@Component({
  selector: 'atcpps-custom-facet',
  templateUrl: './custom-facet.component.html',
  styleUrls: ['./custom-facet.component.scss']
})
export class CustomFacetComponent extends FacetComponent implements OnInit {

 override _facet: Facet;
 protected readonly codec = new HttpUrlEncodingCodec();

  initMin: number;
  initMax: number;
  from: number;
  to: number;
  queryValue: string;
  activeCurrencySymbol?: string;

  constructor(
    facetService: FacetService,
    elementRef: ElementRef<HTMLElement>,
    cd: ChangeDetectorRef,
    private _route: ActivatedRoute,
    private _router: Router,

  ) {
    super(facetService, elementRef, cd)
  }


  ngOnInit(): void {
    if (this._facet && this._facet?.range) {
      this.initMin = this._facet?.range?.min;
      this.initMax = this._facet?.range?.max;
      this.from = this._facet?.range?.from ? this._facet?.range?.from : this._facet?.range.min;
      this.to = this._facet?.range?.to ? this._facet?.range?.to : this._facet?.range.max;
      this.getQueryValue(this._facet);
    }
  }

  protected decodeUriComponentSafe(query: string): string {
   return query?.replace(/%(?![0-9a-fA-F]{2})/g, '%25');
  }

  private getLinkParam(query: string): string {
      return this.codec.decodeValue(this.decodeUriComponentSafe(query)).replace(/\+/g, ' ');
  }
  private constructUrl(query: string, min: number, max: number){
    return query.replace('{from}',min.toString()).replace('{to}', max.toString());
  }

  private getQueryValue(facet: Facet){
    if(facet?.range?.query?.query?.value){
      this.queryValue = this.getLinkParam(facet?.range?.query?.query?.value);
    }
  }

  public rangeFilterChange(event: any) {
    const [min, max] = event.detail;
    const queryUrl = this.constructUrl(this.queryValue, min, max)
    this._router.navigate([], {
      relativeTo: this._route,
      queryParams: {
        query: `${queryUrl}`
      },
      queryParamsHandling: 'merge',
    });
  }
}

