import { Injectable } from '@angular/core';
import { ActivatedRoute, Params, Router, UrlSerializer } from '@angular/router';
import {
  CurrencyService,
  LanguageService,
  RouterState,
  RoutingService,
  ProductSearchService,
  ActivatedRouterStateSnapshot,
} from '@spartacus/core';
import {
  ViewConfig,
  SearchCriteria,
  ProductListComponentService,
} from '@spartacus/storefront';
import { Location } from '@angular/common';
import { BehaviorSubject, Observable, combineLatest } from 'rxjs';
import {
  debounceTime,
  distinctUntilChanged,
  map,
  switchMap,
  take,
  tap,
} from 'rxjs/operators';
import { configPageSize } from 'src/app/spartacus/spartacus-configuration.module';
import { SHOP_FOR_YOUR_CAR_QUERY } from '../../core/constants';
import { CmsService } from '@spartacus/core';

@Injectable({
  providedIn: 'root',
})
export class CustomProductListComponentService extends ProductListComponentService {
  private currentPage = 0;
  vinfilterComponentEnabled = false;
  private initPageLoadSize: number | undefined;
  private isLoadingSubject = new BehaviorSubject<boolean>(false);

  get isLoading$() {
    return this.isLoadingSubject.asObservable();
  }

  constructor(
    protected _productSearchService: ProductSearchService,
    protected _routing: RoutingService,
    protected _activatedRoute: ActivatedRoute,
    protected _currencyService: CurrencyService,
    protected _languageService: LanguageService,
    protected _router: Router,
    protected _config: ViewConfig,
    private urlSerializer: UrlSerializer,
    private location: Location,
    private _cmsService: CmsService
  ) {
    super(
      _productSearchService,
      _routing,
      _activatedRoute,
      _currencyService,
      _languageService,
      _router,
      _config
    );
  }

  override searchByRouting$: Observable<ActivatedRouterStateSnapshot> =
    combineLatest([
      this._routing.getRouterState().pipe(
        distinctUntilChanged((x, y) => {
          // router emits new value also when the anticipated `nextState` changes
          // but we want to perform search only when current url changes
          return x.state.url === y.state.url;
        }),
      ),
      ...[this._languageService.getActive(), this._currencyService.getActive()],
    ]).pipe(
      debounceTime(0),
      switchMap(([routerState, ..._context]) => {
        return this._cmsService
          .getComponentData(
            'AtcppsVinFilterComponent',
            (routerState as RouterState).state.context
          )
          .pipe(
            take(1),
            tap((componebtData) => {
              this.vinfilterComponentEnabled = !!componebtData;
            }),
            map(() => {
              return (routerState as RouterState).state;
            })
          );
      }),
      tap((state: ActivatedRouterStateSnapshot) => {
        const criteria = this.getCriteriaFromRoute(
          state.params,
          state.queryParams
        );

        this.setPageDataFromParams(state.queryParams);
        this.isLoadingSubject.next(true);
        this.search(criteria);
      })
    );

  protected readonly SHOP_FOR_YOUR_CAR = SHOP_FOR_YOUR_CAR_QUERY;
  /**
   * Performs a search based on the given search criteria.
   *
   * The search is delegated to the `ProductSearchService`.
   */
  override search(criteria: SearchCriteria): void {
    const searchCriteria =
      this.getSearchCriteriaForInitialPageLoad(criteria);
    let newCriteria = searchCriteria;
    if (searchCriteria.query === undefined) {
      newCriteria.query = this.SHOP_FOR_YOUR_CAR;
    }
    super.search(newCriteria);
    this.isLoadingSubject.next(false);
  }

  private getSearchCriteriaForInitialPageLoad(
    criteria: SearchCriteria
  ): SearchCriteria {
    const defaultPageSize =
      this._config.view?.defaultPageSize || configPageSize;
    const page = this.initPageLoadSize ? 0 : this.getCurrentPage();
    const pageSize = this.initPageLoadSize
      ? this.initPageLoadSize
      : defaultPageSize;
    const searchCriteria = {
      ...criteria,
      currentPage: page,
      pageSize: pageSize,
    };
    this.initPageLoadSize = undefined;
    return searchCriteria;
  }

  public getVinFilterPageItems(page: number) {
    this.setCurrentPage(page);
    this.getPageItems(page);
  }

  public setCurrentPage(page: number) {
    this.currentPage = page;
  }

  public getCurrentPage(): number {
    return this.currentPage;
  }

  public setPageQueryParam(currentPage: number | undefined): void {
    const urlTree = this._router.createUrlTree([], {
      queryParams: { currentPage: currentPage },
      queryParamsHandling: 'merge',
      relativeTo: this._activatedRoute,
    });
    this.location.go(this.urlSerializer.serialize(urlTree));
  }

  private setPageDataFromParams(params: Params): void {
    this.setCurrentPage(parseInt(params['currentPage'] || '0', 10));
    this.initPageLoadSize =
      (this._config.view?.defaultPageSize || configPageSize) *
      (this.getCurrentPage() + 1);
  }
}

