import { Injectable, OnDestroy } from "@angular/core";
import { Product, ProductSearchPage, ProductSearchService, ProductService, isNotNullable, createFrom, EventService } from "@spartacus/core";
import { SearchResults } from "@spartacus/storefront";
import { Subscription } from "rxjs";
import { filter, withLatestFrom } from "rxjs/operators";
import { CustomProductClickEvent, CustomSearchProductClickEvent } from "src/app/tracking/events/event.models";
import { searchDetails } from "src/app/tracking/gtm/models/gtm.models";
import { GtmDataService } from "src/app/tracking/gtm/services/gtm-data.service";
import { TRACKING_CONSTS } from "src/app/tracking/tracking.constants";

@Injectable({
  providedIn: 'root'
})
export class CustomProductClickEventService implements OnDestroy {
  private productClickPLPSubscription: Subscription;
  private productClickSearchBoxSubscription: Subscription;

  constructor(
    private _eventService: EventService,
    private _gtmDataService: GtmDataService,
    private productService: ProductService,
    private productSearchService: ProductSearchService,
  ) { }

  public dispatchProductClickEvent(productCode: string, index: number, position?: number, title?: string) {
    if (productCode) {
      this.productClickPLPSubscription = this.productService.get(productCode)
        .pipe(
          filter(isNotNullable),
          withLatestFrom(this.productSearchService.getResults())
        )
        .subscribe(([product, productSearchPage]: [Product, ProductSearchPage]) => {
          const productListName: string = this._gtmDataService.getPageCategoryFromTrackingDetails(product?.trackingDetails);

          if (position && title) {
            // Dispatch a CustomProductClickEvent with the transformed event details For Carousel

            this._eventService.dispatch(
              createFrom(
                CustomProductClickEvent, this.transformEventDetails(
                  product,
                  TRACKING_CONSTS.eventActions.sliderProductList + title,
                  TRACKING_CONSTS.listName.carouselPosition + position,
                  index
                )
              )
            )

          } else
            if (productSearchPage.freeTextSearch) {
              // Dispatch a CustomSearchProductClickEvent with the transformed event details For Search Result Page Products

              const search = {
                term: productSearchPage.freeTextSearch,
                autocomplete: productSearchPage.freeTextSearch,
                results: productSearchPage.pagination?.totalResults
              }
              this._eventService.dispatch(
                createFrom(
                  CustomSearchProductClickEvent, this.transformEventDetails(
                    product,
                    TRACKING_CONSTS.eventActions.pageList,
                    TRACKING_CONSTS.listName.productSearch,
                    index,
                    search)
                )
              )
            } else {
              // Dispatch a CustomProductClickEvent with the transformed event details For Product Listing page Products

              this._eventService.dispatch(
                createFrom(
                  CustomProductClickEvent, this.transformEventDetails(
                    product,
                    TRACKING_CONSTS.eventActions.pageList,
                    productListName,
                    index
                  )
                )
              )

            }
        });
    }
  }

  public dispatchProductClickEventFromSearchSuggestions(productCode: string, freeText: string, results: SearchResults) {
    this.productClickSearchBoxSubscription = this.productService.get(productCode)
      .pipe(filter(isNotNullable))
      .subscribe((product: Product) => {
        const index = results.products?.findIndex((item: any) => item.code === productCode);
        const indexation = (index && index > -1) ? index : TRACKING_CONSTS.EMPTY_VALUE;
        const search = {
          term: freeText
        }
        // Dispatch a CustomSearchProductClickEvent with the transformed event details for searchbox suggested products

        this._eventService.dispatch(
          createFrom(
            CustomSearchProductClickEvent, this.transformEventDetails(
              product,
              TRACKING_CONSTS.eventActions.pageList,
              TRACKING_CONSTS.listName.productSearchSuggestion,
              indexation,
              search)
          )
        )
      });
  }

  private transformEventDetails(product: Product, eventAction: string, listName?: string, index?: number, search?: searchDetails) {
    const baseEventDetail: any = {
      event: TRACKING_CONSTS.interactionLabel,
      eventDetail: {
        name: TRACKING_CONSTS.eventNames.selectItem,
        category: TRACKING_CONSTS.eventCategories.link,
        action: eventAction,
        label: TRACKING_CONSTS.eventLabels.viewDetails,
        listName: listName,
        products: [{
          ...this._gtmDataService.transformCoreProduct(product),
          index: index,
          listName: listName,
          qty: TRACKING_CONSTS.defaultQty,
        }],
      },
      application: this._gtmDataService.getPageApplication(),
    };

    if (search) {
      baseEventDetail.eventDetail.search = search;
    }

    return baseEventDetail;
  }


  ngOnDestroy(): void {
    this.productClickPLPSubscription?.unsubscribe();
    this.productClickSearchBoxSubscription?.unsubscribe();
  }
}

import { Component, Input, OnInit } from '@angular/core';
import { VariantOption } from '@spartacus/core';
import { BaseOption, OccConfig, isNotUndefined, Product, ProductScope, ProductService, RoutingService, VariantOptionQualifier, VariantQualifier } from '@spartacus/core';

import { filter, take } from 'rxjs/operators';
@Component({
  selector: 'atcpps-custom-variant-color-size-selector',
  templateUrl: './custom-variant-color-size-selector.component.html',
  styleUrls: ['./custom-variant-color-size-selector.component.scss']
})
export class CustomVariantColorSizeSelectorComponent implements OnInit {

  private baseUrl = '';
  private color2SizeOptionsMap: any = {};

  constructor(
    private config: OccConfig,
    private productService: ProductService,
    private routingService: RoutingService,
  ) { 
    this.baseUrl = this.getBaseUrl();
  }

  @Input()
  product: Product | undefined;

  @Input()
  variants: BaseOption | undefined;

  ngOnInit(): void {
    const variantOptions = this.product?.baseOptions?.length ? this.product?.baseOptions[0]?.options : undefined;
    this.generateColor2SizeOptionsMap(variantOptions);
  }

  private generateColor2SizeOptionsMap(variantOptions: VariantOption[] | undefined) {
    variantOptions?.map((item: VariantOption) => {
      if(item.variantOptionQualifiers){
        const color: string = this.getColorVariantOptionValue(item.variantOptionQualifiers);
        if(this.color2SizeOptionsMap[color]){
          this.color2SizeOptionsMap[color].push(this.getColorMapObj(item));
        }
        else{
          this.color2SizeOptionsMap[color] = [this.getColorMapObj(item)];
        }
      }
    });
  }

  private getColorMapObj(item: VariantOption): any {
    const sizeVariantOption = item?.variantOptionQualifiers?.find((q: any) => q.qualifier === VariantQualifier.SIZE);
    return {
      ...item, 
      ...sizeVariantOption
    };
  }

  protected getUniqueColorVariants(variants: VariantOption[]){
    const uniqueColorVariants: any[] = [];
    for(const key in this.color2SizeOptionsMap){
      if(Object.prototype.hasOwnProperty.call(this.color2SizeOptionsMap, key) && this.color2SizeOptionsMap[key][0]){
        uniqueColorVariants.push({
          code: this.color2SizeOptionsMap[key][0].code,
          variantOptionQualifiers: this.color2SizeOptionsMap[key][0].variantOptionQualifiers,
          sizeVariants: this.color2SizeOptionsMap[key]
        });
      }
    }
    return uniqueColorVariants.length ? uniqueColorVariants : variants;
  }

  protected getUniqueSizeVariants(variants: VariantOption[]) {
    let uniqueColors: any[] = [];
    if(this.variants?.selected?.variantOptionQualifiers){
      const selectedColor = this.getColorVariantOptionValue(this.variants?.selected?.variantOptionQualifiers);
      uniqueColors = variants.filter((item) => {
        if(item?.variantOptionQualifiers){
          const sizeColor = this.getColorVariantOptionValue(item?.variantOptionQualifiers);
          return sizeColor === selectedColor;
        }
        return false;
      });
    }
    return uniqueColors.length ? uniqueColors : variants;
  }

  protected changeSize(code: string): null {
    if (code) {
      this.productService
        .get(code, ProductScope.LIST)
        .pipe(
          // below call might looks redundant but in fact this data is going to be loaded anyways
          // we're just calling it earlier and storing
          filter(isNotUndefined),
          take(1)
        )
        .subscribe((product: Product) => {
          this.routingService.go({
            cxRoute: 'product',
            params: product,
          });
        });
    }
    return null;
  }

  protected changeColor(code: string): null {
    if (code) {
      this.productService
        .get(code, ProductScope.LIST)
        .pipe(
          // below call might looks redundant but in fact this data is going to be loaded anyways
          // we're just calling it earlier and storing
          filter(isNotUndefined),
          take(1)
        )
        .subscribe((product: Product) => {
          this.routingService.go({
            cxRoute: 'product',
            params: product,
          });
        });
    }
    return null;
  }
// FIX ME, we need to find way to mock data when function has protected
 getSizeVariantOptionValue(qualifiers: VariantOptionQualifier[]) {
    const obj = qualifiers.find((q) => q.qualifier === VariantQualifier.SIZE);
    return obj ? obj.value : '';
  }

  protected getColorVariantOptionValue(qualifiers: VariantOptionQualifier[]): string {
    const obj = qualifiers.find((q) => q.qualifier === VariantQualifier.COLOR);
    return obj?.value || '';
  }

  protected getBaseUrl(): string {
    return (
      this.config.backend?.media?.baseUrl ??
      this.config.backend?.occ?.baseUrl ??
      ''
    );
  }

  protected getColorVariantThumbnailUrl(
    variantOptionQualifiers: VariantOptionQualifier[]
  ): string {
    const qualifier = variantOptionQualifiers.find((item) => item.image);
    const imageUrl = qualifier ? `${qualifier.image?.url}` : '';

    return imageUrl && imageUrl.startsWith('http') 
      ? `${imageUrl}`
      : `${this.baseUrl}${imageUrl}`;
  }

}

