import { Injectable } from '@angular/core';
import { AuthConfig } from '../../../config/AuthConfig';
import {
  UserIdService,
  AuthRedirectService,
  StateWithClientAuth,
  AuthService,
} from '@spartacus/core';
import { Store } from '@ngrx/store';
import { EnvironmentProperties } from '../../../common/environment-variables';
import { EnvPickerService } from 'src/app/utilities/env-picker.service';
import { UtilService } from 'src/app/utilities/util.service';
import { RoutingService } from '@spartacus/core';
import { BrowserStorageService } from 'src/app/store/browser-storage.service';
import { StoreKeys } from 'src/app/store/store-keys';
import { Params } from '@angular/router';
import { AppState } from 'src/app/store/app-state';
import { combineLatest } from 'rxjs';
import { ActivatedRoute } from '@angular/router';
import { AuthRedirectStorageService } from '@spartacus/core';
import { WindowRef } from '@spartacus/core';

export enum CiasContext {
  Login = 'login',
  Register = 'register',
}

@Injectable({
  providedIn: 'root',
})
export class UserAuthService {
  constructor(
    private _appState: AppState,
    private _envPicker: EnvPickerService,
    protected userIdService: UserIdService,
    private _authService: AuthService,
    protected authRedirectService: AuthRedirectService,
    private authRedirectStorageService: AuthRedirectStorageService,
    private _utilService: UtilService,
    private _routingService: RoutingService,
    private _browserStorageService: BrowserStorageService,
    protected store: Store<StateWithClientAuth>,
    private route: ActivatedRoute,
    private _winRef: WindowRef
  ) { }

  private _constructAuthUrl(context: string, params?: string): string {
    const endPoint =
      context === CiasContext.Register
        ? AuthConfig.registration
        : AuthConfig.loginEndpoint;
    const orderGuid = params === undefined ? `` : params;
    const appBaseHref = this.getAppBaseHref();

    const countryCode = this._appState.getCurrentCountry();
    const locale = this._utilService.getSiteContext('language');
    return `${EnvironmentProperties[this._envPicker.getCurrentEnvironment()]
        .ciasLoginUrl
      }/${AuthConfig.platform}/${AuthConfig.version}/${endPoint}?locale=${locale
      }&country=${countryCode}&${AuthConfig.mode}&tenantId=${EnvironmentProperties[this._envPicker.getCurrentEnvironment()]
        .ciasTenantId
      }&redirectUrl=${this._utilService.getCurrentUrl()}${orderGuid}`;
  }

  public handleRegisterRedirect(): void {
    combineLatest([this.route.queryParams, this.authRedirectStorageService.getRedirectUrl()]).subscribe(([value1, lastVisitedUrl]) => {
      if (lastVisitedUrl !== undefined) {
        this._browserStorageService.storeItem(
          StoreKeys.LastVisitedUrl,
          lastVisitedUrl
        );
      }
      this.initRegister();
    })
  }

  public handleLoginRedirect(): void {
    combineLatest([this.route.queryParams, this.authRedirectStorageService
      .getRedirectUrl()]).subscribe(([value1, lastVisitedUrl]) => {
        if (lastVisitedUrl !== undefined) {
          this._browserStorageService.storeItem(
            StoreKeys.LastVisitedUrl,
            lastVisitedUrl
          );
        }
        this.initLogin();
        this._browserStorageService.deleteItem(
          StoreKeys.SameAsDeliveryAddress
        );
      })
  }
  public initLogin(): void {
    this._invokeAuthRedirect(this._constructAuthUrl(CiasContext.Login));
  }

  public getAppBaseHref(): string | null {
    return sessionStorage.getItem(StoreKeys.AppBaseHref);
  }

  public initRegister(): void {
    this._invokeAuthRedirect(this._constructAuthUrl(CiasContext.Register));
  }

  public initRegisterWithOrderGuid(orderGuid?: string): void {
    const params =
      orderGuid === undefined
        ? ``
        : `${this.generateGuidQueryParam(orderGuid)}`;
    this._invokeAuthRedirect(
      this._constructAuthUrl(CiasContext.Register, params)
    );
  }

  private _invokeAuthRedirect(authUrl: string): void {
    this._winRef.location.href = authUrl;
  }

  public handleOneTimeToken(params: Params): void {
    if (params['token']) {
      this._browserStorageService.storeItem(
        StoreKeys.OneTimeToken,
        params['token']
      );
      if (params['guid']) {
        this._browserStorageService.storeItem(
          StoreKeys.OrderGuid,
          params['guid']
        );
      }
      this.initLoginSession();
    }
  }

  public initLoginSession() {
    this._authService
      .loginWithCredentials(
        '',
        this._browserStorageService.readItem(StoreKeys.OneTimeToken) as string
      )
      .then(
        () => {
          this._redirectToLastVisitedPage();
        },
        () => {
          //error
        }
      );
  }

  private _redirectToLastVisitedPage(): void {
    if (this._browserStorageService.readItem(StoreKeys.OrderGuid)) {
      this._browserStorageService.deleteItem(StoreKeys.OrderGuid);
      this._routingService.goByUrl('/my-account/orders');
    } else if (this._browserStorageService.readItem(StoreKeys.LastVisitedUrl)) {
      this._routingService.goByUrl(
        this._browserStorageService.readItem(StoreKeys.LastVisitedUrl) || '{}'
      );
      this._browserStorageService.deleteItem(StoreKeys.LastVisitedUrl);
    }
  }

  private generateGuidQueryParam(guid: string): string {
    return this._utilService.encodeUrlParams(`?guid=${guid}`);
  }
}

