  COMPATIBILITY_OUTPUTS,
  VinVehicleData,
} from '../../core/models/vehicle.models';
import { catchError, filter, map, switchMap } from 'rxjs/operators';
import { VehicleStateService } from '../../state/vehicle-state.service';
import { VinFilterService } from '../../core/services/vin-filter.service';

@Component({
  selector: 'atcpps-vehicle-compatibility-wrapper',
  templateUrl: './vehicle-compatibility-wrapper.component.html',
})
export class VehicleCompatibilityWrapperComponent implements OnDestroy, OnInit {
  categoryLink: string | any;
  errorMessageType: string | any;
  product$: Observable<Product | null> = this._currentProductService.getProduct(
    [ProductScope.DETAILS, ProductScope.PRICE]
  );

  compatibiltyCheckTypes: typeof COMPATIBILITY_MATRIX = COMPATIBILITY_MATRIX;
  compatibiltyCheckOutputs: typeof COMPATIBILITY_OUTPUTS =
    COMPATIBILITY_OUTPUTS;
  compatibiltyCheckResult$: any;
  vinVehicleData$: Observable<VinVehicleData | undefined> =
    this._vinVehicleSelectionService.vinVehicleData$;

  constructor(
    private _currentProductService: CurrentProductService,
    private _productCompatibilityService: ProductCompatibilityService,
    private _vinVehicleSelectionService: VehicleStateService,
    private _vehicleStateService: VehicleStateService,
    private _pageMetaService: PageMetaService,
    private _vinFilterService: VinFilterService
  ) {}
  protected subscription = new Subscription();

  ngOnInit(): void {
    this.categoryRoute();
    this.triggerInitialCompatibilityCheck();
  }

  private categoryRoute() {
    this.subscription.add(
      this._pageMetaService.getMeta().subscribe((item: PageMeta | null) => {
        if (item?.breadcrumbs && item?.breadcrumbs?.length >= 2) {
          this.categoryLink = item.breadcrumbs[1].link;
        }
      })
    );
  }

  public updateToggleState(toggleStatus: boolean) {
    this._vinFilterService.updateToggleBtnState(toggleStatus);
  }

  private triggerInitialCompatibilityCheck(): void {
    this.subscription.add(
      this._vinVehicleSelectionService
        .getSelectedVehicle()
        .pipe(
          switchMap((vinVehicleData: VinVehicleData | undefined) => {
            return this.product$.pipe(
              map((product: Product | null) => {
                return { vinVehicleData: vinVehicleData, product: product };
              })
            );
          })
        )
        .subscribe((data: any) => {
          if (data?.vinVehicleData?.vin && data?.product?.code)
            this._triggerCompatibilityCheck(
              data.vinVehicleData,
              data.product.code
            );
        })
    );
  }

  private _clearLastCheck() {
    this._vehicleStateService.setCompatilityCheckResult(undefined);
  }

  onVehicleChanged(
    vehicleData: VinVehicleData | undefined,
    productCode: string
  ): void {
    this.errorMessageType = undefined;
    if (!vehicleData?.vin) this._clearLastCheck();
    if (vehicleData?.vin && productCode)
      this._triggerCompatibilityCheck(vehicleData, productCode);
  }

  private _triggerCompatibilityCheck(
    vehicle: VinVehicleData,
    productCode: string
  ): void {
    this.compatibiltyCheckResult$ = this._productCompatibilityService
      .performCompatibilityCheck(vehicle.vin, productCode)
      .pipe(
        filter(isNotNullable),
        map((data) => {
          return data?.productCompatibilityList[0]?.status;
        }),
        catchError((error: any) => this.handleError(error, vehicle))
      );
  }

  private handleError(error: any, vehicle: VinVehicleData) {
    if (error) {
      if (vehicle) this._clearLastCheck();
      return (this.errorMessageType = error?.error?.errors[0]?.type);
    }
  }

  ngOnDestroy(): void {
    this._clearLastCheck();
    this.subscription.unsubscribe();
  }
}

