export interface TabElement {
  id: string;
  name: string;
  headline: string;
  templateRef: TemplateRef<any> | undefined;
}
@Component({
  selector: 'atcpps-vehicle-selector',
  templateUrl: './vehicle-selector.component.html',
})
export class VehicleSelectorComponent implements OnInit, AfterViewInit, OnDestroy {
  @HostListener('window:resize', ['$event'])
  onResize() {
    this.setComponentContainer();
  }
  @ViewChild('atcppsVinVehicleSelect', { static: true })
  vinVehicleSelectTemplate: TemplateRef<any>;
  public theme = 'light';
  public vehicleSelectorTabs: TabElement[];
  public selectedTab: TabElement | undefined;
  public selectedVinVehileData: VinVehicleData | undefined;
  public selectedTabName: string | undefined;
  public validationInProgress = false;
  vinVehicleData$: Observable<VinVehicleData | undefined> =
    this._vinVehicleSelectionService.vinVehicleData$;
  smallContainer: boolean;
  containerHeight: number;
  lastContainerHeight: number;
  siblingHeight: number;
  subscription = new Subscription();

  constructor(
    private cmsComponentData: CmsComponentData<any>,
    private _vinVehicleSelectionService: VehicleStateService,
    private _flyOutHeaderService: FlyoutHeaderService,
    private _vinFilterService: VinFilterService,
    private _routingService: RoutingService,
    private cd: ChangeDetectorRef,
    private el: ElementRef,
    private _winRef: WindowRef
  ) {}

  ngOnInit(): void {
    this.cmsComponentData.data$.subscribe((cmsComponentData: any) => {
      this.setThemeFromCmsStyleClass(cmsComponentData?.styleClasses);
    });

    this.vehicleSelectorTabs = [
      {
        id: this.getUniqueIdentifier('atcpps-vin-vehicle-select'),
        name: 'atcpps-vin-vehicle-select',
        headline: 'vehicleSelector.tabHeadline',
        templateRef: this.vinVehicleSelectTemplate,
      },
    ];
    this.selectedTab = this.vehicleSelectorTabs[0];
    this.selectedTabName = this.selectedTab?.name;
this.subscription.add(
  this.vinVehicleData$.pipe(
    tap((data: VinVehicleData | undefined) => {
      this.selectedVinVehileData = data;
      this.onVinVehicleDataChanged();
      this.cd?.detectChanges();
    }),
  ).subscribe()
);
  }

  ngAfterViewInit() {
    setTimeout(() => {
      this.setComponentContainer();
    }, 1000);
  }

  private setComponentContainer(): void {
    this.containerHeight = this.el.nativeElement.offsetHeight;
    this.siblingHeight = this.getSmallestSiblingHeight(this.el);
    if (
      !this.smallContainer ||
      this.containerHeight > (this.siblingHeight ?? 0)
    )
      this.lastContainerHeight = this.containerHeight;
    this.smallContainer =
      (this.smallContainer ? this.lastContainerHeight : this.containerHeight) >
      (this.siblingHeight ?? 0);
    this.cd?.detectChanges();
  }

  private getSmallestSiblingHeight(element: ElementRef): number {
    if(element?.nativeElement?.parentNode?.children) {
      const siblings = Array.from(element?.nativeElement?.parentNode?.children);
      const filteredSiblings = siblings.filter(sibling => sibling !== element.nativeElement);
      const heights = filteredSiblings.map(sibling => (sibling as HTMLElement).offsetHeight);
      return heights.length > 0 ? Math.min(...heights) : 0;
    }
    return 0;
  }

  private getUniqueIdentifier(name: string): string {
    const components = this._winRef.document.getElementsByTagName(
      'atcpps-vehicle-selector'
    );
    return `${name}-${components.length}`;
  }

  browseCTAClick(): void {
    this._flyOutHeaderService.toggleFlyout(
      FlyoutheaderComponentsType.VINSELECTOR
    );
    this._vinFilterService.updateToggleBtnState(true);
    this._routingService.go({ cxRoute: 'searchByVehicle' });
  }

  setThemeFromCmsStyleClass(styleClasses: string): void {
    if (styleClasses.includes('dark')) {
      this.theme = 'dark';
    } else if (styleClasses.includes('light')) {
      this.theme = 'light';
    }
  }

  onVinVehicleDataChanged(): void {
    this.reselectTab();
  }

  onValidationInProgress(validationInProgress: boolean) {
    this.validationInProgress = validationInProgress;
  }

  private reselectTab(): void {
    this.selectedTabName = undefined;
    setTimeout(() => {
      this.selectedTabName = this.selectedTab?.name;
      this.cd?.markForCheck();
    });
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }
}

import { Component, OnDestroy, OnInit } from '@angular/core';
