@Component({
  selector: 'atcpps-vin-filter',
  templateUrl: './vin-filter.component.html',
  styleUrls: ['./vin-filter.component.scss'],
})
export class VinFilterComponent implements OnInit, OnDestroy {
  isExpanded = true;
  subscriptions: Subscription;
  compSubscription = new Subscription();
  toggleState$: Observable<boolean> = this._vinFilterService.vinFilterToggle$;
  vinVehicleData$: Observable<VinVehicleData | undefined> =
    this._vinVehicleSelectionService.vinVehicleData$;
  iconTypes = ICON_TYPE;
  constructor(
    private _vinFilterService: VinFilterService,
    private _vinVehicleSelectionService: VehicleStateService
  ) {}
  ngOnDestroy(): void {
    this.compSubscription.unsubscribe();
  }
  ngOnInit(): void {
    this.compSubscription.add(
      this.vinVehicleData$
      .pipe(startWith(null), pairwise())
      .subscribe(([prevVehicleData, currVehicleData]) => {
        if (
          currVehicleData &&
          this._vinFilterService._getCompatibleArticleNumbers(currVehicleData.vin)
            .length === 0
        ) {
          this._loadProductSku(false);
        }
        else {
          (prevVehicleData && !currVehicleData) ?
            this._vinFilterService.fetchFirstPage() :
            this._vinFilterService.refreshListPage();
        }
      })
    );
  }

