    this.isExpanded = !this.isExpanded;
  }

  toggleFilter(event: any) {
    const toggleStatus = event?.target?.checked;
    this._vinFilterService.updateToggleBtnState(toggleStatus);
    this._loadProductSku(true);
  }

  private _loadProductSku(loadFistPage: boolean) {
    this._vinFilterService.loadProductSkus(loadFistPage);
  }
}

import {
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Input,
  OnDestroy,
  OnInit,
  Output,
} from '@angular/core';
import { VehicleStateService } from '../../state/vehicle-state.service';
import { HttpErrorResponse } from '@angular/common/http';
import {
  VEHICLE_DATA_IMG_SIZE,
  VinVehicleData,
} from '../../core/models/vehicle.models';
import { Observable, Subscription } from 'rxjs';
import {
  UntypedFormBuilder,
  UntypedFormGroup,
  Validators,
} from '@angular/forms';
import { tap } from 'rxjs/operators';

@Component({
  selector: 'atcpps-vin-vehicle-selection',
  templateUrl: './vin-vehicle-selection.component.html',
})
export class VinVehicleSelectionComponent implements OnInit, OnDestroy {
  @Input() theme = 'light';
  @Input() id = 'input-id';
  @Input() imageSize = VEHICLE_DATA_IMG_SIZE.MEDIUM;
  @Output() changed = new EventEmitter();
  @Output() manuallyChanged = new EventEmitter();
  @Output() validationInProgress = new EventEmitter();
  @Input() placeHolderText: string;
  defaultImagePath = './assets/images/placeholder_limousine-right.webp';
  vinInput = '';
  isValidVin: boolean | undefined;
  vinVehicleData$: Observable<VinVehicleData | undefined> =
    this._vehicleSelectionService.vinVehicleData$;
  vinValidationForm: UntypedFormGroup;
  vinErrorMessage = false;
  vinVehicleSubscription: Subscription;
  vehicleImageUrl: string;
  previousVehicleData: VinVehicleData | undefined;

  constructor(
    private _vehicleSelectionService: VehicleStateService,
    private fb: UntypedFormBuilder,
    private cd: ChangeDetectorRef
  ) {
    this.vinVehicleSubscription = this.vinVehicleData$?.subscribe(
      (vehicleData: VinVehicleData | undefined) => {
        if (vehicleData) {
          this.vehicleImageUrl = this._getImageByIndex(
            vehicleData,
            this.imageSize
          );
        }
        if (this.hasDataChanged(vehicleData)) {
          this.changed.emit(vehicleData);
          this.previousVehicleData = vehicleData;
        }
      }
    );
  }

  ngOnInit(): void {
    this.buildForm();
  }

  private hasDataChanged(vehicleData: VinVehicleData | undefined) {
    return (
      JSON.stringify(vehicleData) !== JSON.stringify(this.previousVehicleData)
    );
  }

  private buildForm(): void {
    this.vinValidationForm = this.fb.group({
      vinInput: ['', [Validators.required, Validators.minLength(17)]],
    });

    this.vinValidationForm
      .get('vinInput')
      ?.valueChanges.subscribe((value: string) => {
        this.showErrorMessage(false);
        if (value?.length === 17) {
          this.validateEnteredVin(value);
        }
      });
  }

  private validateEnteredVin(vin: string): void {
    this._vehicleSelectionService
      .validateVin(vin)
      .pipe(tap(() => this.validationInProgress.emit(true)))
      .subscribe((res: any) => this.setVinValidationResult(res));
  }

  private setVinValidationResult = (
    result: VinVehicleData | HttpErrorResponse
  ) => {
    if (result instanceof HttpErrorResponse) {
      this.isValidVin = false;
      this.validationInProgress.emit(false);
      if (result.status !== 200) {
        this.showErrorMessage(true);
      }
    } else if (result?.vin) {
      this.isValidVin = true;
      this.showErrorMessage(false);
      this.validationInProgress.emit(false);
      this.updateVinVehicleData(result);
      this.manuallyChanged.emit();
    }
  };

  private showErrorMessage(show: boolean): void {
    this.vinErrorMessage = show;
    this.cd.markForCheck();
  }

  private updateVinVehicleData(data: VinVehicleData): void {
    this._vehicleSelectionService.updateVehicleData(data);
  }

  public clearSelectedVinVehicleData(): void {
    this.isValidVin = undefined;
    this._vehicleSelectionService.clearVehicleData();
    this.vinValidationForm.reset();
  }

  private _getImageByIndex(
    vehicleData: VinVehicleData,
    size: VEHICLE_DATA_IMG_SIZE
  ) {
    const imageEntries = vehicleData?.images?.entry;
    if (!imageEntries) return '';
    const entry = imageEntries.find((item) => item.key === size);
    return entry?.value ?? '';
  }

  ngOnDestroy(): void {
    this.vinVehicleSubscription?.unsubscribe();
  }
}

