import { Injectable, } from '@angular/core';
import { HttpRequest, HttpEvent, HttpHandler, HttpInterceptor } from '@angular/common/http';
import { Observable } from 'rxjs';
import { SiteContextParamsService } from '@spartacus/core';

@Injectable({
  providedIn: 'root'
})
export class CustomHttpAuthInterceptorService implements HttpInterceptor {
  constructor(private _siteContextParamsService: SiteContextParamsService) { }
  intercept(
    request: HttpRequest<any>,
    next: HttpHandler
  ): Observable<HttpEvent<any>> {

      if (request.url.includes('authorizationserver/oauth/token')) {

        if (localStorage.getItem('oneTimeToken')) {

          const oneTimeToken = localStorage.getItem('oneTimeToken');
          const currentSite= this._siteContextParamsService.getParamValues('baseSite');
          if(localStorage.getItem('guid')){

            const guid = localStorage.getItem('guid');
            request = request.clone({
              body: request.body.delete("grant_type").
                      delete("scope").
                      delete("username").
                      delete("password").
                      append("grant_type","ciasToken").
                      append("oneTimeToken",oneTimeToken).
                      append("guid",guid).
                      append("currentSiteId",currentSite)
          });
          //localStorage.removeItem('guid');
          }
          else {
            request = request.clone({
              body: request.body.delete("grant_type").
                      delete("scope").
                      delete("username").
                      delete("password").
                      append("grant_type","ciasToken").
                      append("oneTimeToken",oneTimeToken).
                      append("currentSiteId",currentSite)
          });
          }
            localStorage.removeItem('oneTimeToken');
        }
      }

    return next.handle(request);
  }
}

