import { Injectable } from '@angular/core';
import { createFrom } from '@spartacus/core';
import { EventService } from '@spartacus/core';
import { filter, map, switchMap, withLatestFrom } from 'rxjs/operators';
import {
  Cart,
  CartAddEntrySuccessEvent,
  CartRemoveEntrySuccessEvent,
  CartUiEventAddToCart,
  CartUpdateEntrySuccessEvent,
  DeleteCartEvent,
} from '@spartacus/cart/base/root';
import {
  CustomAddToCartEvent,
  CustomDeleteCartEvent,
  CustomRemoveFromCartEvent,
  CustomUpdateCartSuccessEvent,
} from '../event.models';
import { GtmDataService } from '../../gtm/services/gtm-data.service';
import { TRACKING_CONSTS } from '../../tracking.constants';
import {
  AnalyticsCartDetails,
  AnalyticsCartEvent,
  AnalyticsProduct,
} from '../../gtm/models/gtm.models';
import { Observable } from 'rxjs';
import { PageEvent } from '@spartacus/storefront';
import { ProductDetailsPageEvent } from '@spartacus/storefront';
import { CurrentProductService } from '@spartacus/storefront';
import { Product } from '@spartacus/core';
import { ActiveCartService } from '@spartacus/cart/base/core';

@Injectable({
  providedIn: 'root',
})
export class CustomCartEventService {
  constructor(
    private _eventService: EventService,
    private _gtmDataService: GtmDataService,
    private _currentProductService: CurrentProductService,
    private _activeCartService: ActiveCartService
  ) {
    this.register();
  }

  protected register() {
    this.registerEvent(
      CustomAddToCartEvent,
      this.buildCartAddEntrySuccessEvent()
    );

    this.registerEvent(
      CustomRemoveFromCartEvent,
      this.buildCartRemoveEntrySuccessEvent()
    );

    this.registerEvent(
      CustomUpdateCartSuccessEvent,
      this.buildCartUpdateEntrySuccessEvent()
    );

    this.registerEvent(
      CustomDeleteCartEvent,
      this.buildDeleteCartSuccessEvent()
    );
  }

  private registerEvent(eventType: any, observable: Observable<any>) {
    this._eventService.register(eventType, observable);
  }

  private buildCartAddEntrySuccessEvent(): Observable<CustomAddToCartEvent> {
    // cartUiEventAddToCart - This event is intended for the UI feedback like the added to cart dialog.
    return this._eventService?.get(CartUiEventAddToCart).pipe(
      switchMap(() => {
        return this._eventService?.get(CartAddEntrySuccessEvent).pipe(
          withLatestFrom(this._currentProductService.getProduct()),
          map(([cartAddEntrySuccessEvent, product]) => {
            return this.createCustomAddToCartEvent(
              cartAddEntrySuccessEvent,
              product
            );
          })
        );
      })
    );
  }

  private buildCartRemoveEntrySuccessEvent(): Observable<CustomRemoveFromCartEvent> {
    return this._eventService?.get(CartRemoveEntrySuccessEvent).pipe(
      withLatestFrom(
        this._eventService?.get(PageEvent),
        this._currentProductService.getProduct(),
      ),
      map(([cartRemoveEntrySuccessEvent, pageEvent, product]) => {
        const eventDetails: AnalyticsCartDetails =
          this.getRemoveEntryEventDetails(
            cartRemoveEntrySuccessEvent,
            pageEvent,
            product
          );

        return createFrom(
          CustomRemoveFromCartEvent,
          this.generateEventObject(eventDetails)
        );
      })
    );
  }

  private buildCartUpdateEntrySuccessEvent(): Observable<CustomUpdateCartSuccessEvent> {
    return this._eventService?.get(CartUpdateEntrySuccessEvent).pipe(
      withLatestFrom(
        this._eventService?.get(PageEvent),
        this._currentProductService.getProduct(),
      ),
      map(([cartUpdateEntrySuccessEvent, pageEvent, product]) => {
        return this.createCartUpdateCartSuccessEvent(
          cartUpdateEntrySuccessEvent,
          pageEvent,
          product
        );
      })
    );
  }

  private buildDeleteCartSuccessEvent(): Observable<CustomDeleteCartEvent> {
    return this._eventService?.get(DeleteCartEvent).pipe(
      withLatestFrom(this._activeCartService.getActive()),
      filter(([ , cart]) => Boolean(cart.totalItems)),
      map(([ , cart]) => {
        return this.createCustomDeleteCartEvent(cart);
      })
    );
  }

  private createCustomAddToCartEvent(
    cartAddEntrySuccessEvent: CartAddEntrySuccessEvent,
    product: Product | null
  ): AnalyticsCartEvent {
    const addToCartDetais = this.getAddToCartDetails(
      cartAddEntrySuccessEvent,
      product
    );
    return createFrom(
      CustomAddToCartEvent,
      this.generateEventObject(addToCartDetais)
    );
  }

  private createCustomDeleteCartEvent(cart: Cart): AnalyticsCartEvent {
    const eventDetails: AnalyticsCartDetails =
      this.getDeleteCartEventDetails(cart);
    return createFrom(
      CustomDeleteCartEvent,
      this.generateEventObject(eventDetails)
    );
  }

  private createCartUpdateCartSuccessEvent(
    cartUpdateEntrySuccessEvent: CartUpdateEntrySuccessEvent,
    pageEvent: PageEvent,
    product: Product | null
  ): AnalyticsCartEvent {
    const eventDetails: AnalyticsCartDetails = this.getUpdateEntryEventDetails(
      cartUpdateEntrySuccessEvent,
      pageEvent,
      product
    );

    return createFrom(
      CustomUpdateCartSuccessEvent,
      this.generateEventObject(eventDetails)
    );
  }

  private getAddToCartDetails(
    event: CartAddEntrySuccessEvent,
    product: Product | null
  ): AnalyticsCartDetails {
    const productData = product || event?.entry?.product;
    const products = productData
      ? [
          this._gtmDataService.transformCartProduct(
            productData,
            event?.quantityAdded
          ),
        ]
      : [];

    return {
      name: TRACKING_CONSTS.eventNames.addToCart,
      category: TRACKING_CONSTS.eventCategories.feature,
      action: TRACKING_CONSTS.eventActions.productDetail,
      label: TRACKING_CONSTS.eventLabels.addToCart,
      products: products,
    };
  }

  private getUpdateEntryEventDetails(
    cartEvent: CartUpdateEntrySuccessEvent,
    pageEvent: PageEvent,
    product: Product | null
  ): AnalyticsCartDetails {
    const { entry, quantity } = cartEvent;
    const entryQty = entry?.quantity || 0;
    const isIncrease = quantity > entryQty;
    const productData = product || {
      ...(entry?.product || {}),
      price: entry?.basePrice,
    };
    const eventName = isIncrease
      ? TRACKING_CONSTS.eventNames.addToCart
      : TRACKING_CONSTS.eventNames.removeFromCart;
    const eventCategory = TRACKING_CONSTS.eventCategories.feature;
    const eventAction = this.getEventActionFromPageEvent(pageEvent);
    const eventLabel = isIncrease
      ? TRACKING_CONSTS.eventLabels.addToCartUpdate
      : TRACKING_CONSTS.eventLabels.removeFromCartUpdate;
    const quantityChanged = isIncrease
      ? quantity - entryQty
      : entryQty - quantity;

    return {
      name: eventName,
      category: eventCategory,
      action: eventAction,
      label: eventLabel,
      products: [
        this._gtmDataService.transformCartProduct(productData, quantityChanged),
      ],
    };
  }

  private getRemoveEntryEventDetails(
    cartEvent: CartRemoveEntrySuccessEvent,
    pageEvent: PageEvent,
    product: Product | null
  ) {
    const { entry } = cartEvent;
    const quantityChanged = cartEvent?.entry?.quantity || 0;
    const productData = product || {
      ...(entry?.product || {}),
      price: entry?.basePrice,
    };
    const eventName = TRACKING_CONSTS.eventNames.removeFromCart;
    const eventCategory = TRACKING_CONSTS.eventCategories.feature;
    const eventAction = this.getEventActionFromPageEvent(pageEvent);
    const eventLabel = TRACKING_CONSTS.eventLabels.removeFromCart;

    return {
      name: eventName,
      category: eventCategory,
      action: eventAction,
      label: eventLabel,
      products: [
        this._gtmDataService.transformCartProduct(productData, quantityChanged),
      ],
    };
  }

  private getDeleteCartEventDetails(cart: Cart) {
    const { entries } = cart;
    const productData = entries?.map((entry) => {
      const { product, quantity, basePrice } = entry;
      const transformedProduct: AnalyticsProduct = {
        ...this._gtmDataService.transformCartProduct(product, quantity),
        pricewtax: basePrice?.value || TRACKING_CONSTS.EMPTY_VALUE,
      };
      return transformedProduct;
    });
    const eventName = TRACKING_CONSTS.eventNames.removeFromCart;
    const eventCategory = TRACKING_CONSTS.eventCategories.feature;
    const eventAction = TRACKING_CONSTS.eventActions.cartPage;
    const eventLabel = TRACKING_CONSTS.eventLabels.clearCart;

    return {
      name: eventName,
      category: eventCategory,
      action: eventAction,
      label: eventLabel,
      products: productData,
    };
  }

  private getEventActionFromPageEvent(pageEvent: PageEvent): string {
    const isProductDetailPage = pageEvent instanceof ProductDetailsPageEvent;
    const eventAction = isProductDetailPage
      ? TRACKING_CONSTS.eventActions.addToCartModal
      : TRACKING_CONSTS.eventActions.cartPage;
    return eventAction;
  }

  private generateEventObject(
    eventDetail: AnalyticsCartDetails
  ): AnalyticsCartEvent {
    return {
      event: TRACKING_CONSTS.interactionLabel,
      eventDetail: eventDetail,
      application: this._gtmDataService.getPageApplication(),
    };
  }
}

import { Injectable } from '@angular/core';
import {
  EventService,
  CmsService,
  createFrom,
  isNotNullable,
  ProductSearchService,
  ProductSearchPage,
  Breadcrumb,
} from '@spartacus/core';
import {
  NavigationEvent,
  CurrentProductService,
  CategoryPageResultsEvent,
  SearchPageResultsEvent,
} from '@spartacus/storefront';
import { Observable, combineLatest, zip } from 'rxjs';
import {
  switchMap,
  map,
  filter,
  take,
  first,
  withLatestFrom,
  pairwise,
  startWith,
  distinctUntilChanged,
} from 'rxjs/operators';
import {
  CustomCheckoutPageEvent,
  CustomOrderPlacedEvent,
  CustomProductCategoryListPageEvent,
  CustomProductDetailsPageEvent,
  CustomProductSearchListPageEvent,
  GenericNavigationEvent,
} from '../event.models';
import { PageView } from '../../gtm/models/gtm.models';
import { TRACKING_CONSTS } from '../../tracking.constants';
import { GtmDataService } from '../../gtm/services/gtm-data.service';
import { OrderFacade } from '@spartacus/order/root';
import { ActiveCartService } from '@spartacus/cart/base/core';
import { CheckoutQueryFacade } from '@spartacus/checkout/base/root';
import { PageEvent } from '@spartacus/storefront';
import { UtilService } from 'src/app/utilities/util.service';

@Injectable({
  providedIn: 'root',
})
export class CustomNavigationEventBuilder {
  constructor(
    private _cmsService: CmsService,
    private _eventService: EventService,
    private _productService: CurrentProductService,
    private _gtmDataService: GtmDataService,
    private _orderService: OrderFacade,
    private _activeCartService: ActiveCartService,
    private _checkoutQueryFacade: CheckoutQueryFacade,
    private _productSearchService: ProductSearchService,
    private _utilService: UtilService
  ) {
    this.register();
  }

  protected register(): void {
    this._eventService.register(
      GenericNavigationEvent,
      this.buildGenericNavigationEvent()
    );
    this._eventService.register(
      CustomProductDetailsPageEvent,
      this.buildProductDetailsPageEvent()
    );
    this._eventService.register(
      CustomProductCategoryListPageEvent,
      this.buildProductCategoryListPageEvent()
    );
    this._eventService.register(
      CustomProductSearchListPageEvent,
      this.buildProductSearchListPageEvent()
    );
    this._eventService.register(
      CustomOrderPlacedEvent,
      this.buildOrderPlacedEvent()
    );
    this._eventService.register(
      CustomCheckoutPageEvent,
      this.buildCheckoutPageEvent()
    );
  }

  protected buildGenericNavigationEvent(): Observable<GenericNavigationEvent> {
    return this._eventService.get(NavigationEvent).pipe(
      switchMap((navigationEvent: NavigationEvent) => {
        return this._cmsService.getPage(navigationEvent.context).pipe(
          first(),
          filter((page) => !!page),
          map((page: any) => {
            return createFrom(GenericNavigationEvent, {
              ...this._constructPageView(
                page.analyticsPageName,
                page.analyticsPageType
              ),
            });
          })
        );
      })
    );
  }

  protected buildProductDetailsPageEvent(): Observable<CustomProductDetailsPageEvent> {
    const productDetailPageNavigation =
      this._getListingPageNavigationEvent('product_detail');
    const currentProduct = this._productService
      .getProduct()
      .pipe(filter(isNotNullable));

    return zip(productDetailPageNavigation, currentProduct).pipe(
      map(([navigationEvent, product]) => {
        navigationEvent.page.products = [
          this._gtmDataService.transformCoreProduct(product),
        ];
        return createFrom(CustomProductDetailsPageEvent, {
          ...navigationEvent,
        });
      })
    );
  }

  protected buildProductCategoryListPageEvent(): Observable<CustomProductCategoryListPageEvent> {
    const productListPageNavEvent =
      this._getListingPageNavigationEvent('product_list');
    const filterSearchRes = this._getProductSearchResult();
    const categoryPageVisited = this._eventService
      .get(CategoryPageResultsEvent)
      .pipe(
        withLatestFrom(
          this._eventService.get(PageEvent).pipe(
            startWith(null), // https://github.com/ReactiveX/rxjs/issues/4772
            pairwise()
          ),
          this._getPairedGenericNavigationEvent()
        ),
        distinctUntilChanged(
          (
            [previouslyEmittedCategoryPage],
            [
              currentCategoryPage,
              [previousRoute, currentRoute],
              [prevNavigation],
            ]: [
              CategoryPageResultsEvent,
              [PageEvent | null, PageEvent | null],
              [GenericNavigationEvent | null, GenericNavigationEvent | null]
            ]
          ) => {
            const prevNavType = prevNavigation?.page?.type;
            const isDuplicatedPageView =
              previouslyEmittedCategoryPage.categoryCode ===
                currentCategoryPage.categoryCode &&
              previousRoute?.navigation.semanticRoute ===
                currentRoute?.navigation.semanticRoute;
            // Allow navigating back from SRP
            if (previousRoute instanceof SearchPageResultsEvent) return false;
            // Allow navigating from any other page (faq, cms, etc.)
            if (isDuplicatedPageView && prevNavType) {
              const resolvedPageTypes = [
                'product_list',
                'product_detail',
                'homepage',
              ];
              if (!resolvedPageTypes.includes(prevNavType)) return false;
            }
            return isDuplicatedPageView;
            // A true means that this item is not unique. If category code is the same AND last emitted semantic route
            // is the same then this is a duplicate.
          }
        ),
        map(([categoryPageEvent]: any) => {
          return categoryPageEvent;
        })
      );

    return categoryPageVisited.pipe(
      withLatestFrom(productListPageNavEvent, filterSearchRes),
      filter(([, , filterSearchRes]) => {
        return Boolean(filterSearchRes);
      }),
      map(
        ([categoryPageResultsEvent, navigationEvent]: [
          CategoryPageResultsEvent,
          GenericNavigationEvent,
          boolean
        ]) => {
          navigationEvent.page.listName = categoryPageResultsEvent.categoryCode;
          navigationEvent.page.name = `${navigationEvent.page.type}.${categoryPageResultsEvent.categoryCode}`;
          return createFrom(CustomProductCategoryListPageEvent, {
            ...navigationEvent,
          });
        }
      )
    );
  }

  protected buildProductSearchListPageEvent(): Observable<CustomProductSearchListPageEvent> {
    const productSearchPageNavEvent =
      this._getListingPageNavigationEvent('search_results');
    const searchPageVisited = this._eventService
      .get(SearchPageResultsEvent)
      .pipe(
        //skip(1),
        withLatestFrom(this._getPairedGenericNavigationEvent()),
        distinctUntilChanged(
          (
            [previouslyEmittedSearchPage],
            [currentSearchPage, [prevNavigation, currNavigation]]: [
              SearchPageResultsEvent,
              [GenericNavigationEvent | null, GenericNavigationEvent | null]
            ]
          ) => {
            const isDuplicatedSearchPage =
              previouslyEmittedSearchPage.searchTerm ===
                currentSearchPage.searchTerm &&
              (prevNavigation?.page?.name === currNavigation?.page.name ||
                prevNavigation === null);

            return isDuplicatedSearchPage;
          }
        ),
        map(
          ([currentSearchPage]: [
            SearchPageResultsEvent,
            [GenericNavigationEvent | null, GenericNavigationEvent | null]
          ]) => {
            return currentSearchPage;
          }
        )
      );
    const filterSearchRes = this._getProductSearchResult();

    return searchPageVisited.pipe(
      withLatestFrom(productSearchPageNavEvent, filterSearchRes),
      filter(([, , filterSearchRes]) => Boolean(filterSearchRes)),
      map(([searchPageResultsEvent, navigationEvent]) => {
        navigationEvent.page.search = {
          term: searchPageResultsEvent.searchTerm,
          autocomplete: searchPageResultsEvent.searchTerm,
          results: '' + searchPageResultsEvent.numberOfResults,
        };
        return createFrom(CustomProductSearchListPageEvent, {
          ...navigationEvent,
        });
      })
    );
  }

  protected buildOrderPlacedEvent(): Observable<CustomOrderPlacedEvent> {
    const orderConfirmationPageNavEvent =
      this._getListingPageNavigationEvent('order_confirmation');
    return orderConfirmationPageNavEvent.pipe(
      switchMap((navigationEvent: GenericNavigationEvent) => {
        return this._orderService.getOrderDetails().pipe(
          map((order) => {
            navigationEvent.page.transaction =
              this._gtmDataService.transformTransactionData(order);
            return createFrom(CustomOrderPlacedEvent, {
              ...navigationEvent,
            });
          })
        );
      })
    );
  }

  protected buildCheckoutPageEvent(): Observable<CustomCheckoutPageEvent> {
    return this._eventService.get(GenericNavigationEvent).pipe(
      filter((navigationEvent: GenericNavigationEvent) =>
        this._gtmDataService.isCheckoutPage(navigationEvent.page.name)
      ),
      switchMap((navigationEvent: GenericNavigationEvent) => {
        return combineLatest([
          this._activeCartService.getActive(),
          this._checkoutQueryFacade.getCheckoutDetailsState().pipe(
            filter((state) => !state.loading),
            map((state) => state.data)
          ),
        ]).pipe(
          take(1),
          map(([cartObject, checkoutObject]) => {
            navigationEvent.page.cart = this._gtmDataService.transformCartData(
              cartObject,
              checkoutObject
            );
            return createFrom(CustomCheckoutPageEvent, {
              ...navigationEvent,
            });
          })
        );
      })
    );
  }

  private _getListingPageNavigationEvent(pageName: string) {
    return this._eventService
      .get(GenericNavigationEvent)
      .pipe(
        filter(
          (navigationEvent: GenericNavigationEvent) =>
            navigationEvent.page.name === pageName
        )
      );
  }

  private _constructPageView(
    pageName: string | undefined,
    pageType: string | undefined
  ): PageView {
    const pageView: PageView = {
      event: TRACKING_CONSTS.pageViewLabel,
      page: {
        name: pageName,
        type: this._isLogin(pageName)
          ? TRACKING_CONSTS.LOGIN_PAGE_TYPE
          : pageType,
      },
      application: this._gtmDataService.getPageApplication(),
    };
    return pageView;
  }

  private _isLogin(pageName: string | undefined): boolean {
    return (
      pageName === 'login_register' &&
      this._utilService.getCurrentUrl().indexOf('?forced=') === -1
    );
  }

  private _getProductSearchResult() {
    return this._productSearchService.getResults().pipe(
      pairwise(),
      withLatestFrom(this._getPairedGenericNavigationEvent()),
      map(
        ([[prev, curr], [prevNavEvent, currNavEvent]]: [
          [ProductSearchPage, ProductSearchPage],
          [GenericNavigationEvent | null, GenericNavigationEvent | null]
        ]) => {
          const detectFacetOrSortingChange = this._detectFacetOrSortingChange(
            prev,
            curr
          );
          const isFreeTextSearch = this._isFreeTextSearch(
            prev?.freeTextSearch,
            curr?.freeTextSearch
          );
          if (prevNavEvent === null) {
            return true;
          } else if (currNavEvent?.page?.search && !isFreeTextSearch) {
            return false;
          }
          return isFreeTextSearch || detectFacetOrSortingChange;
        }
      )
    );
  }

  private _getPairedGenericNavigationEvent() {
    return this._eventService
      .get(GenericNavigationEvent)
      .pipe(startWith(null), pairwise());
  }

  private _detectFacetOrSortingChange(
    prev: ProductSearchPage,
    curr: ProductSearchPage
  ): boolean {
    if (!prev || Object.keys(prev).length === 0) {
      return true;
    }
    const toggledFilter =
      this._getToggledBreadcrumb(curr.breadcrumbs, prev.breadcrumbs) ||
      this._getToggledBreadcrumb(prev.breadcrumbs, curr.breadcrumbs);
    const isCategoryChange = this._isCategoryChange(toggledFilter);
    const isActiveSortingChange =
      !isCategoryChange && this._isSortingChange(prev, curr);
    const isFilterChange = isCategoryChange ? false : toggledFilter;

    return !isActiveSortingChange && !isFilterChange;
  }

  private _isFreeTextSearch(
    prevSearch: string | undefined,
    currSearch: string | undefined
  ): boolean {
    if (currSearch) {
      return prevSearch !== currSearch;
    } else {
      return false;
    }
  }

  private _isCategoryChange(filter: Breadcrumb | undefined): boolean {
    return filter?.facetCode === 'allCategories';
  }

  private _isSortingChange(
    prev: ProductSearchPage,
    curr: ProductSearchPage
  ): boolean {
    return prev?.pagination?.sort !== curr?.pagination?.sort;
  }

  private _getToggledBreadcrumb(
    bc1: Breadcrumb[] | undefined,
    bc2: Breadcrumb[] | undefined
  ): Breadcrumb | undefined {
    let toggled = undefined;
    if (bc1 && bc2) {
      toggled = bc1.find(
        (x) =>
          !bc2.find(
            (y) =>
              y.facetCode === x.facetCode &&
              y.facetValueCode === x.facetValueCode
          )
      );
    }
    return toggled;
  }
}

import { Injectable } from '@angular/core';
import { 
  EventService, 
  ProductSearchPage, 
  createFrom, 
  ProductSearchService,
  RouterState,
  LanguageService,
  CurrencyService,
  RoutingService,
  ActivatedRouterStateSnapshot
} from '@spartacus/core';
import { Observable, combineLatest } from 'rxjs';
import { CustomViewItemListEvent } from '../event.models';
import { debounceTime, distinctUntilChanged, filter, map, skip, switchMap } from 'rxjs/operators';
import { GtmDataService } from '../../gtm/services/gtm-data.service';
import { TRACKING_CONSTS } from '../../tracking.constants';
import { configPageSize } from 'src/app/spartacus/spartacus-configuration.module';

@Injectable({
  providedIn: 'root'
})
export class CustomViewItemListEventService {
  private unregisterEvent: any;

  constructor(
    private _eventService: EventService,
    private _gtmDataService: GtmDataService,
    private _productSearchService: ProductSearchService,
    private _routing: RoutingService,
    private _currencyService: CurrencyService,
    private _languageService: LanguageService,
  ) { 
    this.register();
  }

  public register() {
    this.unregisterEvent = this.registerEvent(
      CustomViewItemListEvent,
      this.buildViewItemListEvent()
    );
  }

  public unregister() {
    if(this.unregisterEvent) this.unregisterEvent();    
  }

  private registerEvent(eventType: any, observable: Observable<any>) {
    this._eventService.register(eventType, observable);
  }

  private buildViewItemListEvent(): Observable<CustomViewItemListEvent> {
    return combineLatest([
      this._routing.getRouterState().pipe(
        distinctUntilChanged((x, y) => {
          // router emits new value also when the anticipated `nextState` changes
          // but we want to perform search only when current url changes
          return x.state.url === y.state.url;
        })
      ),
      ...[this._languageService.getActive(), this._currencyService.getActive()],
    ])
    .pipe(
      debounceTime(0),
      map(([routerState, ..._context]) => (routerState as RouterState).state),
      switchMap((result: ActivatedRouterStateSnapshot) => {
        return this._productSearchService.getResults()
        .pipe(
          skip(1),
          filter((result: ProductSearchPage) => Boolean(
            Object.keys(result).length > 0 && 
            (result.freeTextSearch || result?.breadcrumbs?.length)
          )),
          map((result: ProductSearchPage) => {
            return createFrom(
              CustomViewItemListEvent,
              this.generateEventObjectFromPageSearch(result)
            );
        }))
      })
    );
  }

  private generateEventObjectFromPageSearch(result: ProductSearchPage): CustomViewItemListEvent {
    const productSearch = TRACKING_CONSTS.listName.productSearch;
    const category = (result?.breadcrumbs?.length) ? this.getCategoryFromBreadcrumb(result.breadcrumbs) : TRACKING_CONSTS.EMPTY_VALUE;
    const listName = result?.freeTextSearch ? productSearch : category;
    const lastIndex = ((result?.pagination?.currentPage ?? 0) * (result?.pagination?.pageSize ?? configPageSize));
    return {
      event: TRACKING_CONSTS.interactionLabel,
      eventDetail: {
        name: TRACKING_CONSTS.eventNames.viewItemList,
        category: TRACKING_CONSTS.eventCategories.impression,
        action: TRACKING_CONSTS.eventActions.pageList,
        label: listName,
        listName: listName, 
        products: this._gtmDataService.getViewItemListProducts(result?.products, listName, lastIndex)
      },
      application: this._gtmDataService.getPageApplication(),
    };
  }

  private getCategoryFromBreadcrumb(breadcrumbs: any[]): string | undefined {
    const categoryIndex = [...breadcrumbs].reverse()
      .findIndex(breadcrumb => breadcrumb.facetCode === 'category');

    const allCategoriesIndex = breadcrumbs
      .findIndex(breadcrumb => breadcrumb.facetCode === 'allCategories');

    return categoryIndex !== -1
      ? breadcrumbs[(breadcrumbs.length - 1) - categoryIndex].facetValueCode
      : (allCategoriesIndex !== -1
        ? breadcrumbs[allCategoriesIndex].facetValueCode
        : TRACKING_CONSTS.EMPTY_VALUE);
  }
}

import { Injectable } from '@angular/core';
import { GtmDataService } from './gtm-data.service';
import { CxEvent, WindowRef, AuthService } from '@spartacus/core';
import {
  TmsCollector,
  TmsCollectorConfig,
  WindowObject,
} from '@spartacus/tracking/tms/core';
import { GtmCollectorService } from '@spartacus/tracking/tms/gtm';
import { UtilService } from '../../../utilities/util.service';
import { LoggedInStatus } from '../../../common/constants';
import { Observable, Subscription } from 'rxjs';
import { EventService } from '@spartacus/core';
import { NavigationEvent } from '@spartacus/storefront';
import { switchMap } from 'rxjs/operators';
import { GlobalGTMEvent } from '../models/gtm.models';


@Injectable({
  providedIn: 'root',
})
export class CustomGtmColletorService
  extends GtmCollectorService
  implements TmsCollector
{
  private subGlobalPushEvent= new Subscription();
  constructor(
    private _eventService: EventService,
    private _authService: AuthService,
    private _utilService: UtilService,
    windowRef: WindowRef,
    private _gtmDataService: GtmDataService
  ) {
    super(windowRef);
  }

  override init(config: TmsCollectorConfig, windowObject: WindowObject): void {
    super.init(config, windowObject);
    this.initGlobalPushEvent(config, windowObject);
  }

  map?<T extends CxEvent>(event: T): T | object {
    return this._gtmDataService.transformEvent(event);
  }

  private initGlobalPushEvent(
    config: TmsCollectorConfig,
    windowObject: WindowObject
  ): void {
    const obsUserLoggedIn: Observable<boolean> =
      this._authService.isUserLoggedIn();
    const obsNavigationEvent: Observable<NavigationEvent> =
      this._eventService.get(NavigationEvent);
    let navigationEventRes: NavigationEvent;

    this.subGlobalPushEvent = obsNavigationEvent
      .pipe(
        switchMap((res: NavigationEvent) => {
          navigationEventRes = res;
          return obsUserLoggedIn;
        })
      )
      .subscribe((isUserLoggedIn: boolean) => {
        if (
          navigationEventRes &&
          navigationEventRes.url &&
          !navigationEventRes.url.includes('token=')
        ) {
          super.pushEvent(
            config,
            windowObject,
            this.getGlobalGTMPayload(isUserLoggedIn)
          );
          this.subGlobalPushEvent.unsubscribe();
        }
        return isUserLoggedIn;
      });
  }

  private getGlobalGTMPayload(isUserLoggedIn: boolean): GlobalGTMEvent {
    return {
      event: 'global',
      site: {
        market: this._utilService.getActiveCountryIsoCode(),
        language: this._utilService.getSiteContext('language').split('_')[0],
        currency: this._utilService.getSiteContext('currency'),
        mediaQuery: this._utilService.getMediaQuery(),
        sysEnv: this._utilService.getEnvProps().gtmEnvName,
      },
      user: {
        loginState: isUserLoggedIn
          ? LoggedInStatus.isLoggedIn
          : LoggedInStatus.isLoggesOut,
      },
      application: {
        id: this._utilService.getApplicationId(),
        version: this._utilService.getApplicationVersion(),
      },
    };
  }

  override pushEvent<T extends CxEvent>(
    config: TmsCollectorConfig,
    windowObject: WindowObject,
    event: T | any
  ): void {
    const dataLayerProperty = config.dataLayerProperty ?? 'dataLayer';
    if (!this._utilService.isEmptyObject(event))
      windowObject[dataLayerProperty].push(event);
  }
}

import { Injectable, isDevMode } from '@angular/core';
import { CxEvent } from '@spartacus/core';
import { TmsCollector, TmsService } from '@spartacus/tracking/tms/core';
import { GenericNavigationEvent } from '../../events/event.models';
import { CategoryPageResultsEvent } from '@spartacus/storefront';
import { SearchPageResultsEvent } from '@spartacus/storefront';
import { TRACKING_CONSTS } from '../../tracking.constants';

@Injectable({
  providedIn: 'root',
})
export class CustomTmsService extends TmsService {
  /**
   * Called only once to start collecting and dispatching events
   */
  override collect(): void {
    if (!this.windowRef.isBrowser()) {
      return;
    }

    for (const tmsCollectorConfig in this.tmsConfig.tagManager) {
      if (!(tmsCollectorConfig in this.tmsConfig.tagManager)) {
        continue;
      }

      const collectorConfig =
        this.tmsConfig.tagManager[tmsCollectorConfig] ?? {};

      if (!collectorConfig.collector) {
        if (isDevMode()) {
          console.warn(
            `Skipping the '${tmsCollectorConfig}', as the collector is not defined.`
          );
        }
        continue;
      }

      const events =
        collectorConfig.events?.map((event) => this.eventsService.get(event)) ||
        [];
      const collector = this.injector.get<TmsCollector>(
        collectorConfig.collector
      );
      // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
      collector.init(collectorConfig, this.windowRef.nativeWindow!);

      this.subscription.add(
        this.mapEvents(events).subscribe((event) => {
          if (this._toBeIgnored(event)) return;
          if (collectorConfig.debug) {
            console.log(
              ` Pushing the following event to ${tmsCollectorConfig}: `,
              event
            );
          }

          event = collector.map ? collector.map(event) : event;
          collector.pushEvent(
            collectorConfig,
            // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
            this.windowRef.nativeWindow!,
            event
          );
        })
      );
    }
  }

  private _toBeIgnored<T extends CxEvent | any>(event: T): boolean {
    if (
      event instanceof GenericNavigationEvent &&
      this._shouldIgnoreGenericEvent(event.page.name, event.page.type)
    )
      return true;
    if (event instanceof CategoryPageResultsEvent) return true;
    if (event instanceof SearchPageResultsEvent) return true;
    return false;
  }

  private _shouldIgnoreGenericEvent(
    pageName: string | undefined,
    pageType: string | undefined
  ): boolean {
    return (
      pageName !== 'login_register' &&
      pageName !== 'guest_checkout' &&
      TRACKING_CONSTS.customNavigationPages.some(
        (element) => element === pageType
      )
    );
  }
}

import { Injectable } from '@angular/core';
import {
  Cart,
  DeliveryMode,
  PaymentDetails,
  Voucher,
} from '@spartacus/cart/base/root';
import { CxEvent } from '@spartacus/core';
import {
  AnalyticsProduct,
  AnalyticsTransactionDetails,
  PageApplication,
} from '../models/gtm.models';
import { Product } from '@spartacus/core';
import { Order } from '@spartacus/order/root';
import { TRACKING_CONSTS } from '../../tracking.constants';
import { CheckoutState } from '@spartacus/checkout/base/root';
import { UtilService } from 'src/app/utilities/util.service';
import { TrackingDetails } from 'src/app/features/product/models/mb.product.model';
import { VariantOptionQualifier } from '@spartacus/core';
import { VariantQualifier } from '@spartacus/core';

@Injectable({
  providedIn: 'root',
})
export class GtmDataService {
  constructor(private _utilService: UtilService) { }

  public transformEvent<T extends CxEvent>(event: any): T | object {
    const eventObject = Object.assign({} as any, event);
    //FIXME This deep cloning has to be checked later!
    const transformedEvent = JSON.parse(JSON.stringify(eventObject));
    return transformedEvent;
  }

  public transformCoreProduct(product: Product | null): AnalyticsProduct {
    const transformedProduct: AnalyticsProduct = {
      name: product?.trackingDetails?.name
        ? product?.trackingDetails.name
        : product?.trackingDetails?.variantName,
      id: product?.trackingDetails?.id,
      category: product?.trackingDetails?.category
        ? product?.trackingDetails.category
        : TRACKING_CONSTS.EMPTY_VALUE,
      type: product?.trackingDetails?.type
        ? product.trackingDetails.type
        : TRACKING_CONSTS.EMPTY_VALUE,
      brand: product?.trackingDetails?.brand
        ? product.trackingDetails.brand
        : TRACKING_CONSTS.EMPTY_VALUE,
      variantId: product?.trackingDetails?.variantId
        ? product.trackingDetails.variantId
        : TRACKING_CONSTS.EMPTY_VALUE,
      variantName: product?.trackingDetails?.variantName
        ? product.trackingDetails.variantName
        : TRACKING_CONSTS.EMPTY_VALUE,
      pricewtax: product?.price?.value
        ? product?.price?.value
        : TRACKING_CONSTS.EMPTY_VALUE,
      size: product?.trackingDetails?.size
        ? product.trackingDetails.size
        : TRACKING_CONSTS.EMPTY_VALUE,
      color: product?.trackingDetails?.color
        ? product.trackingDetails.color
        : TRACKING_CONSTS.EMPTY_VALUE,
    };
    return transformedProduct;
  }

  private getVariant(product: Product, variantQualifier: VariantQualifier) {
    const variant = product?.baseOptions?.[0]?.selected?.variantOptionQualifiers?.filter((variant: VariantOptionQualifier) => {
      return variant.qualifier === variantQualifier
    })
    return variant?.[0]?.value
  }

  public transformTransactionData(order?: Order): AnalyticsTransactionDetails {
    const transformIncomingData: AnalyticsTransactionDetails = {
      id: order?.code,
      qty: order?.totalItems,
      pricewtax: order?.totalPriceWithTax?.value,
      tax: order?.totalTax?.value,
      shipcost: order?.deliveryCost?.value,
      voucher: this.transformVoucherApplied(order?.appliedVouchers),
      products: this.transformProducts(
        order?.entries,
        order?.paymentInfo,
        order?.deliveryMode
      ),
    };
    return transformIncomingData;
  }

  public transformCartData(
    cart: Cart,
    checkoutObject: CheckoutState | undefined
  ): AnalyticsTransactionDetails {
    const transformedCartData: AnalyticsTransactionDetails = {
      id: cart?.code,
      qty: cart?.totalItems,
      pricewtax: cart?.totalPriceWithTax?.value,
      products: this.transformProducts(
        cart?.entries,
        checkoutObject?.paymentInfo,
        checkoutObject?.deliveryMode
      ),
    };
    return transformedCartData;
  }

  private transformVoucherApplied(
    vouchers: Voucher[] | undefined
  ): string | undefined {
    return vouchers
      ? vouchers.map((voucher) => voucher.code).join(' | ')
      : TRACKING_CONSTS.EMPTY_VALUE;
  }

  private transformProducts(
    products?: any[],
    paymentInfo?: PaymentDetails,
    deliveryMode?: DeliveryMode
  ): AnalyticsProduct[] | undefined {
    const productsEntries = products?.map((entries) => {
      const { product, quantity, basePriceWithTax } = entries;
      const transformedProduct: AnalyticsProduct = {
        ...this.transformCoreProduct(product),
        pricewtax: basePriceWithTax,
        qty: quantity,
        shipmethod: deliveryMode?.code
          ? deliveryMode.code
          : TRACKING_CONSTS.EMPTY_VALUE,
        paymethod: paymentInfo?.cardType?.code
          ? paymentInfo.cardType.code
          : TRACKING_CONSTS.EMPTY_VALUE,
      };
      return transformedProduct;
    });
    return productsEntries;
  }

  public isCheckoutPage(pageName: string | undefined): boolean {
    return TRACKING_CONSTS.checkoutPages.some(
      (element) => element === pageName
    );
  }

  public getPageApplication(): PageApplication {
    return {
      id: this._utilService.getApplicationId(),
      version: this._utilService.getApplicationVersion(),
    };
  }

  public transformCartProduct(product: any, quantity: number | undefined) {
    return {
      ...this.transformCoreProduct(product),
      qty: quantity ? quantity : TRACKING_CONSTS.EMPTY_VALUE,
    };
  }

  public getPageCategoryFromTrackingDetails(trackingDetails: TrackingDetails): string {
    const category = trackingDetails?.category ? trackingDetails.category.split('/')[0] : '';
    return category;
  }

  public getViewItemListProducts(products: Product[] | undefined, listName: string | undefined, lastIndex: number, loadedProducts?: Product[]): AnalyticsProduct[] {
    if(!products) return [];
    return [
      ...products.map((product, index) => {
        const pageIndex = typeof lastIndex === 'number' ? (index + lastIndex) : index;
        const carouselIndex = loadedProducts?.findIndex((item: any) => item.code === product.code);
        const transformedProduct: AnalyticsProduct = {
          ...this.transformCoreProduct(product),
          qty: 1,
          listName: listName,
          index: typeof carouselIndex === 'number' ? carouselIndex : pageIndex
        };
        return transformedProduct;
      })
    ];
  }
}

import { Injectable } from '@angular/core';
import { OccEndpointsService } from '@spartacus/core';
import { APP_CONSTS, Environments } from '../common/constants';
import { Meta } from '@angular/platform-browser';

@Injectable({
  providedIn: 'root',
})
export class EnvPickerService {
  constructor(
    private _meta: Meta,
    protected _occEnpointService: OccEndpointsService
  ) {}

  public getOccBaseUrl(): string {
    return this._occEnpointService.getBaseUrl();
  }

  public getCurrentEnvironment(): string {
    let currentEnv = '';
    const backendUrl = (this._meta.getTag('name=occ-backend-base-url') as any)
      .content;
    if (backendUrl === 'OCC_BACKEND_BASE_URL_VALUE') {
      currentEnv = Environments.Local;
    }
    if (backendUrl.includes(APP_CONSTS.occEnvEndpoints.dev)) {
      currentEnv = Environments.Dev;
    }
    if (backendUrl.includes(APP_CONSTS.occEnvEndpoints.stage)) {
      currentEnv = Environments.Staging;
    }
    if (backendUrl.includes(APP_CONSTS.occEnvEndpoints.prod)) {
      currentEnv = Environments.Production;
    }

    return currentEnv;
  }
}
